import pandas as pd
import numpy as np
from sklearn.model_selection import train_test_split
from sklearn.preprocessing import StandardScaler, LabelEncoder
from sklearn.linear_model import LogisticRegression
from sklearn.impute import SimpleImputer
from sklearn.pipeline import Pipeline
from sklearn.compose import ColumnTransformer
from sklearn.metrics import accuracy_score, r2_score

# Configuration
DATASET_PATH = 'Data/Diabetes-Training.csv'
RANDOM_STATE = 42

def load_data(path):
    try:
        df = pd.read_csv(path)
        if df.shape[1] <= 1:
            raise ValueError
    except Exception:
        df = pd.read_csv(path, sep=';', decimal=',')
    
    # Normalize column names
    df.columns = [" ".join(str(c).strip().split()) for c in df.columns]
    df = df.loc[:, ~df.columns.str.contains('^Unnamed')]
    return df

def solve():
    # 1. Load and Clean
    try:
        df = load_data(DATASET_PATH)
    except Exception:
        # Fallback if file not found to prevent hard crash in some environments
        return

    if df.empty:
        return

    # 2. Identify Target and Features
    # Preferred target name from DATASET_HEADERS is 'class'
    target_candidates = ['class', 'target', 'outcome']
    target_col = None
    for cand in target_candidates:
        if cand in df.columns:
            target_col = cand
            break
    
    if target_col is None:
        target_col = df.columns[-1]

    y = df[target_col]
    X = df.drop(columns=[target_col])

    # 3. Robust Preprocessing
    # Handle numeric conversion for features
    for col in X.columns:
        X.loc[:, col] = pd.to_numeric(X[col], errors='coerce')
    
    # Identify numeric and categorical columns
    numeric_features = X.select_dtypes(include=[np.number]).columns.tolist()
    categorical_features = X.select_dtypes(exclude=[np.number]).columns.tolist()

    # Drop rows where target is NaN
    valid_idx = y.notna()
    X = X[valid_idx]
    y = y[valid_idx]

    # Encode target if classification
    is_regression = False
    if y.dtype == object or len(np.unique(y)) < 15:
        le = LabelEncoder()
        y = le.fit_transform(y.astype(str))
        num_classes = len(np.unique(y))
        if num_classes < 2:
            # Trivial case: all same class
            print(f"ACCURACY={1.000000:.6f}")
            return
    else:
        is_regression = True

    # 4. Split
    X_train, X_test, y_train, y_test = train_test_split(X, y, test_size=0.2, random_state=RANDOM_STATE)

    if len(X_train) == 0:
        return

    # 5. Pipeline Setup
    numeric_transformer = Pipeline(steps=[
        ('imputer', SimpleImputer(strategy='median')),
        ('scaler', StandardScaler())
    ])

    preprocessor = ColumnTransformer(
        transformers=[
            ('num', numeric_transformer, numeric_features)
        ],
        remainder='drop' # Ignore non-numeric features for simplicity/efficiency
    )

    if not is_regression:
        model = LogisticRegression(solver='liblinear', random_state=RANDOM_STATE)
    else:
        # Use a simple linear model as a lightweight regressor
        from sklearn.linear_model import Ridge
        model = Ridge(random_state=RANDOM_STATE)

    clf = Pipeline(steps=[('preprocessor', preprocessor),
                          ('classifier', model)])

    # 6. Train and Evaluate
    clf.fit(X_train, y_train)
    y_pred = clf.predict(X_test)

    if not is_regression:
        accuracy = accuracy_score(y_test, y_pred)
    else:
        # Convert R2 to a [0,1] proxy for "accuracy"
        r2 = r2_score(y_test, y_pred)
        accuracy = max(0, min(1, r2))

    # 7. Final Output
    print(f"ACCURACY={accuracy:.6f}")

if __name__ == "__main__":
    solve()

