from __future__ import annotations

import numpy as np
import pandas as pd
from scipy.io import arff
from sklearn.ensemble import RandomForestClassifier
from sklearn.metrics import accuracy_score
from sklearn.model_selection import train_test_split

RANDOM_SEED = 42
ARFF_PATH = "dataset_adult.arff"
TARGET_DUMMY_COL = "income_>50K"


def load_arff_to_dataframe(path: str) -> pd.DataFrame:
    data, _ = arff.loadarff(path)
    df = pd.DataFrame(data)

    obj_cols = df.select_dtypes(include=["object"]).columns
    if len(obj_cols) > 0:
        df[obj_cols] = df[obj_cols].apply(
            lambda s: s.str.decode("utf-8") if s.dtype == object else s
        )
    return df


def robust_read_csv(path: str) -> pd.DataFrame:
    df = pd.read_csv(path)
    if df.shape[1] <= 1:
        df_alt = pd.read_csv(path, sep=";", decimal=",")
        if df_alt.shape[1] > df.shape[1]:
            df = df_alt
    return df


def load_dataset(path: str) -> pd.DataFrame:
    p = path.lower()
    if p.endswith(".arff"):
        return load_arff_to_dataframe(path)
    if p.endswith(".csv"):
        return robust_read_csv(path)
    return pd.read_csv(path)


def build_xy(df: pd.DataFrame) -> tuple[pd.DataFrame, pd.Series]:
    df_encoded = pd.get_dummies(df, drop_first=True)

    if TARGET_DUMMY_COL not in df_encoded.columns:
        candidates = [c for c in df_encoded.columns if c.startswith("income_")]
        if len(candidates) == 1:
            target_col = candidates[0]
        else:
            raise KeyError(
                f"Target column '{TARGET_DUMMY_COL}' not found. Available income-like columns: {candidates}"
            )
    else:
        target_col = TARGET_DUMMY_COL

    y = df_encoded[target_col]
    X = df_encoded.drop(columns=[target_col], copy=False)
    return X, y


def train_and_evaluate(X: pd.DataFrame, y: pd.Series) -> float:
    X_train, X_test, y_train, y_test = train_test_split(
        X, y, test_size=0.3, random_state=RANDOM_SEED
    )

    clf = RandomForestClassifier(
        random_state=RANDOM_SEED,
        n_estimators=100,
        n_jobs=-1,
    )
    clf.fit(X_train, y_train)

    y_pred = clf.predict(X_test)
    return float(accuracy_score(y_test, y_pred))


def main() -> None:
    np.random.seed(RANDOM_SEED)
    df = load_dataset(ARFF_PATH)
    X, y = build_xy(df)
    accuracy = train_and_evaluate(X, y)
    print(f"ACCURACY={accuracy:.6f}")


if __name__ == "__main__":
    main()

