import pandas as pd
import numpy as np
from sklearn.preprocessing import StandardScaler, LabelEncoder
from sklearn.model_selection import train_test_split
from sklearn.compose import ColumnTransformer
from sklearn.metrics import accuracy_score
from tensorflow.keras.models import Sequential
from tensorflow.keras.layers import Dense, InputLayer
from tensorflow.keras.utils import to_categorical
import tensorflow as tf

tf.random.set_seed(0)
np.random.seed(0)

data = pd.read_csv('heart_failure.csv')
if data.shape[1] <= 2:
    data = pd.read_csv('heart_failure.csv', sep=';', decimal=',')

target_col = 'death_event' if 'death_event' in data.columns else 'DEATH_EVENT'
feature_cols = ['age', 'anaemia', 'creatinine_phosphokinase', 'diabetes',
                'ejection_fraction', 'high_blood_pressure', 'platelets',
                'serum_creatinine', 'serum_sodium', 'sex', 'smoking', 'time']
feature_cols = [c for c in feature_cols if c in data.columns]

y = data[target_col]
x = data[feature_cols]

X_train, X_test, Y_train, Y_test = train_test_split(x, y, test_size=0.3, random_state=0)

numeric_cols = ['age', 'creatinine_phosphokinase', 'ejection_fraction',
                'platelets', 'serum_creatinine', 'serum_sodium', 'time']
numeric_cols = [c for c in numeric_cols if c in feature_cols]

ct = ColumnTransformer([("numeric", StandardScaler(), numeric_cols)], remainder='passthrough')
X_train = ct.fit_transform(X_train).astype(np.float32)
X_test = ct.transform(X_test).astype(np.float32)

le = LabelEncoder()
Y_train_enc = le.fit_transform(Y_train.astype(str))
Y_test_enc = le.transform(Y_test.astype(str))
Y_train_cat = to_categorical(Y_train_enc)
Y_test_cat = to_categorical(Y_test_enc)

model = Sequential([
    InputLayer(shape=(X_train.shape[1],)),
    Dense(12, activation='relu'),
    Dense(2, activation='softmax')
])
model.compile(loss='categorical_crossentropy', optimizer='adam', metrics=['accuracy'])

model.fit(X_train, Y_train_cat, epochs=100, batch_size=16, verbose=0)

y_estimate = np.argmax(model.predict(X_test, verbose=0), axis=1)
y_true = np.argmax(Y_test_cat, axis=1)

accuracy = accuracy_score(y_true, y_estimate)
print(f"ACCURACY={accuracy:.6f}")

