import pandas as pd
import numpy as np
from sklearn.feature_extraction.text import TfidfVectorizer
from sklearn.model_selection import train_test_split
from sklearn.naive_bayes import MultinomialNB
from sklearn.pipeline import Pipeline
from sklearn.metrics import accuracy_score

# Robust CSV loading
df = None
for kwargs in [dict(), dict(sep=';', decimal=',')]:
    try:
        df = pd.read_csv("data.csv", **kwargs)
        if df.shape[1] < 2:
            raise ValueError("Too few columns")
        break
    except Exception:
        continue

if df is None:
    try:
        df = pd.read_csv("data.csv", sep='\t')
    except Exception:
        pass

assert df is not None and not df.empty, "Failed to load dataset"

# Normalize column names
df.columns = [c.strip().replace("  ", " ") for c in df.columns]
df = df[[c for c in df.columns if not c.startswith("Unnamed")]]

# Identify text and label columns
text_col = None
label_col = None

if "text" in df.columns:
    text_col = "text"
if "label" in df.columns:
    label_col = "label"

# Fallback: find text-like and label-like columns
if text_col is None:
    for c in df.columns:
        if df[c].dtype == object and df[c].str.len().mean() > 20:
            text_col = c
            break
if text_col is None:
    for c in df.columns:
        if df[c].dtype == object:
            text_col = c
            break

if label_col is None:
    for c in df.columns:
        if c != text_col:
            label_col = c
            break

assert text_col is not None and label_col is not None, "Cannot identify text and label columns"

# Drop rows with missing text or label
df = df.dropna(subset=[text_col, label_col]).reset_index(drop=True)
df[text_col] = df[text_col].astype(str)

assert len(df) > 0, "Dataset empty after dropping NaNs"

# Check number of classes
n_classes = df[label_col].nunique()
assert n_classes >= 2, "Need at least 2 classes for classification"

X = df[text_col]
y = df[label_col]

# Train/test split with fixed random state
X_train, X_test, y_train, y_test = train_test_split(
    X, y, test_size=0.25, random_state=42, stratify=y if n_classes >= 2 else None
)

assert len(X_train) > 0 and len(X_test) > 0, "Train or test split is empty"

# Energy-efficient pipeline: TF-IDF + MultinomialNB
# MultinomialNB is extremely lightweight and well-suited for text classification
# TfidfVectorizer with sublinear_tf and max_features cap to limit memory/compute
pipeline = Pipeline([
    ("tfidf", TfidfVectorizer(
        max_features=50000,
        sublinear_tf=True,
        strip_accents="unicode",
        ngram_range=(1, 2),
        max_df=0.95,
        min_df=2,
        dtype=np.float32
    )),
    ("clf", MultinomialNB(alpha=0.1))
])

pipeline.fit(X_train, y_train)
y_pred = pipeline.predict(X_test)
accuracy = accuracy_score(y_test, y_pred)

print(f"ACCURACY={accuracy:.6f}")

# OPTIMIZATION SUMMARY
# - MultinomialNB chosen over LinearSVC/LogisticRegression for maximum energy efficiency:
#   it trains in a single pass (O(n)) with minimal memory, ideal for text classification.
