import pandas as pd
import numpy as np
from sklearn.ensemble import RandomForestRegressor, RandomForestClassifier
from sklearn.model_selection import train_test_split
from sklearn.metrics import accuracy_score

def main():
    path = 'data/AirQualityUCI.csv'
    
    try:
        df = pd.read_csv(path, sep=';', decimal=',')
        if 'Date' not in df.columns or df['Date'].isna().all():
            raise ValueError
    except:
        df = pd.read_csv(path, sep=',', decimal='.')
    
    df = df.dropna(how='all', axis=1).dropna(how='all', axis=0)
    df.columns = df.columns.str.strip()
    
    df = df.dropna(subset=['Date', 'Time'])
    df['Datetime'] = pd.to_datetime(
        df['Date'] + ' ' + df['Time'].str.replace('.', ':', regex=False), 
        dayfirst=True, 
        errors='coerce'
    )
    df = df.set_index('Datetime').drop(columns=['Date', 'Time'], errors='ignore')
    
    features = ['CO(GT)', 'NOx(GT)', 'NO2(GT)', 'C6H6(GT)', 'T', 'RH']
    df = df[features].replace(-200, np.nan).apply(pd.to_numeric, errors='coerce')
    
    daily = df.resample('D').mean().dropna().astype('float32')
    
    np.random.seed(42)
    daily['Hospital_Visits'] = (
        daily['CO(GT)'] * 2.5 + 
        daily['NO2(GT)'] * 0.8 + 
        daily['C6H6(GT)'] * 4.0 + 
        np.random.normal(0, 1, len(daily))
    ).abs()
    
    daily['Risk_Label'] = (daily['Hospital_Visits'] > daily['Hospital_Visits'].median()).astype(np.int8)
    
    X = daily[features]
    y_reg = daily['Hospital_Visits']
    y_clf = daily['Risk_Label']
    
    X_train, X_test, y_reg_train, _, y_clf_train, y_clf_test = train_test_split(
        X, y_reg, y_clf, test_size=0.2, random_state=42
    )
    
    reg_model = RandomForestRegressor(n_estimators=100, random_state=42)
    reg_model.fit(X_train, y_reg_train)
    
    clf_model = RandomForestClassifier(n_estimators=100, random_state=42)
    clf_model.fit(X_train, y_clf_train)
    
    y_clf_pred = clf_model.predict(X_test)
    accuracy = accuracy_score(y_clf_test, y_clf_pred)
    
    print(f"ACCURACY={accuracy:.6f}")

if __name__ == "__main__":
    main()

