def _normalize_columns(cols):
    normed = []
    for c in cols:
        c2 = re.sub(r"\s+", " ", str(c).strip())
        normed.append(c2)
    return normed


def _drop_unnamed(df):
    drop_cols = [c for c in df.columns if re.match(r"^Unnamed:\s*\d+$", str(c)) or str(c).strip().lower() in {"", "unnamed"}]
    if drop_cols:
        df = df.drop(columns=drop_cols, errors="ignore")
    return df


def _read_csv_robust(path):
    # Try default read_csv, then fallback for European CSV conventions if it likely parsed incorrectly.
    try:
        df = pd.read_csv(path)
    except Exception:
        df = None

    def _looks_wrong(d):
        if d is None or d.empty:
            return True
        # If only one column and it contains separators, likely wrong separator
        if d.shape[1] == 1:
            sample = str(d.iloc[0, 0]) if len(d) else ""
            if (";" in sample) or ("," in sample):
                return True
        return False

    if _looks_wrong(df):
        try:
            df = pd.read_csv(path, sep=";", decimal=",")
        except Exception:
            pass
    return df


def _find_dataset_file():
    # Prefer common dataset file names; otherwise any CSV in repo.
    cwd = os.getcwd()
    candidates = []
    for pat in ("**/*.csv", "**/*.CSV"):
        candidates.extend(glob.glob(os.path.join(cwd, pat), recursive=True))

    # Prefer files likely to be Iris dataset; skip obvious non-data files.
    preferred = []
    for p in candidates:
        base = os.path.basename(p).lower()
        if any(tok in base for tok in ("iris", "data", "dataset")) and not any(tok in base for tok in ("sample", "submission", "pred")):
            preferred.append(p)
    ordered = preferred + [p for p in candidates if p not in preferred]

    # Filter out tiny/empty files
    ordered2 = []
    for p in ordered:
        try:
            if os.path.getsize(p) > 0:
                ordered2.append(p)
        except Exception:
            continue
    return ordered2[0] if ordered2 else None


def _coerce_numeric_series(s):
    # Convert to numeric safely; keep NaNs where conversion fails
    return pd.to_numeric(s, errors="coerce")


def _choose_target(df):
    # Prefer non-constant column with low-ish cardinality (likely class label), else a numeric non-constant column.
    if df.shape[1] < 2:
        return None

    # Candidates exclude fully empty columns
    non_empty_cols = []
    for c in df.columns:
        if df[c].notna().any():
            non_empty_cols.append(c)
    if len(non_empty_cols) < 2:
        return None

    n = len(df)
    best = None
    best_score = -1

    for c in non_empty_cols:
        s = df[c]
        nunique = s.nunique(dropna=True)
        if nunique < 2:
            continue
        # Scoring:
        # - Prefer object/category with few unique values (classification)
        # - Penalize ID-like very high cardinality
        is_obj = (s.dtype == "object") or (str(s.dtype).startswith("category"))
        # if numeric but integer-like with few unique, it might still be class
        num_s = _coerce_numeric_series(s)
        num_non_na = num_s.notna().sum()
        is_numeric = num_non_na > 0 and num_non_na >= int(0.5 * n)
        # Unique ratio
        ur = nunique / max(1, n)
        score = 0.0
        if is_obj:
            score += 3.0
        if is_numeric and nunique <= 20:
            score += 2.0
        if nunique <= 10:
            score += 1.5
        # Penalize ID-like
        score -= 5.0 * ur
        # Slightly prefer later columns typical of label position
        score += 0.01 * (list(df.columns).index(c))
        if score > best_score:
            best_score = score
            best = c

    if best is not None:
        return best

    # Fallback: choose a numeric non-constant column
    numeric_cols = []
    for c in non_empty_cols:
        num_s = _coerce_numeric_series(df[c])
        if num_s.notna().sum() >= max(2, int(0.5 * n)) and num_s.nunique(dropna=True) >= 2:
            numeric_cols.append(c)
    return numeric_cols[0] if numeric_cols else non_empty_cols[-1]


def _build_preprocessor(X):
    # Determine categorical vs numerical based on dtype after minimal probing
    numeric_features = []
    categorical_features = []
    for c in X.columns:
        s = X[c]
        if pd.api.types.is_numeric_dtype(s):
            numeric_features.append(c)
        else:
            # If many values can be coerced to numeric, treat as numeric
            num_s = _coerce_numeric_series(s)
            if num_s.notna().sum() >= max(3, int(0.8 * len(X))):
                numeric_features.append(c)
                X[c] = num_s
            else:
                categorical_features.append(c)

    numeric_transformer = Pipeline(
        steps=[
            ("imputer", SimpleImputer(strategy="median")),
            ("scaler", StandardScaler(with_mean=True, with_std=True)),
        ]
    )

    categorical_transformer = Pipeline(
        steps=[
            ("imputer", SimpleImputer(strategy="most_frequent")),
            ("onehot", OneHotEncoder(handle_unknown="ignore", sparse_output=True)),
        ]
    )

    preprocessor = ColumnTransformer(
        transformers=[
            ("num", numeric_transformer, numeric_features),
            ("cat", categorical_transformer, categorical_features),
        ],
        remainder="drop",
        sparse_threshold=0.3,
    )
    return preprocessor, numeric_features, categorical_features


def _is_classification_target(y):
    # Decide task robustly: object/category => classification; numeric with small unique => classification; else regression.
    y = y.dropna()
    if y.empty:
        return False
    if (y.dtype == "object") or (str(y.dtype).startswith("category")):
        return True
    y_num = _coerce_numeric_series(y)
    if y_num.notna().sum() >= max(2, int(0.8 * len(y))) and y_num.nunique(dropna=True) <= 20:
        return True
    return False


def _bounded_regression_score(y_true, y_pred):
    # Stable proxy in [0,1]: 1 / (1 + RMSE / (std + eps))
    y_true = np.asarray(y_true, dtype=float)
    y_pred = np.asarray(y_pred, dtype=float)
    err = y_true - y_pred
    rmse = float(np.sqrt(np.nanmean(err * err)))
    std = float(np.nanstd(y_true))
    eps = 1e-12
    return float(1.0 / (1.0 + (rmse / (std + eps))))


