from sklearn.linear_model import LogisticRegression
from sklearn.metrics import accuracy_score

# --- Robust CSV Loading ---
df = None
try:
    df = pd.read_csv("Iris.csv")
    if df.shape[1] < 3:
        df = pd.read_csv("Iris.csv", sep=';', decimal=',')
except Exception:
    try:
        df = pd.read_csv("Iris.csv", sep=';', decimal=',')
    except Exception as e:
        raise RuntimeError(f"Failed to load dataset: {e}")

# --- Column name normalization ---
df.columns = df.columns.str.strip().str.replace(r'\s+', ' ', regex=True)
df = df.loc[:, ~df.columns.str.startswith('Unnamed')]

# --- Schema detection ---
expected_features = ['SepalLengthCm', 'SepalWidthCm', 'PetalLengthCm', 'PetalWidthCm']
expected_target = 'Species'

available_features = [c for c in expected_features if c in df.columns]

if expected_target in df.columns:
    target_col = expected_target
else:
    # Fallback: pick last non-numeric or last column
    obj_cols = df.select_dtypes(include='object').columns.tolist()
    if obj_cols:
        target_col = obj_cols[-1]
    else:
        target_col = df.columns[-1]

if not available_features:
    # Fallback: use all numeric columns except target and Id-like columns
    available_features = [c for c in df.select_dtypes(include=[np.number]).columns
                          if c.lower() not in ('id', target_col.lower())]

# Drop Id column if present (not a useful feature)
if 'Id' in available_features:
    available_features.remove('Id')

assert len(available_features) >= 1, "No feature columns available"
