# --- Preprocessing ---
# Coerce features to numeric
for col in available_features:
    df[col] = pd.to_numeric(df[col], errors='coerce')

# Drop rows with NaN/inf in features or missing target
df = df.replace([np.inf, -np.inf], np.nan)
df = df.dropna(subset=available_features + [target_col])
assert len(df) > 10, "Dataset too small after cleaning"

X = df[list(available_features)]
