from sklearn.linear_model import LogisticRegression
from sklearn.metrics import accuracy_score
from sklearn.pipeline import Pipeline

# Robust CSV Loading
def load_data(path):
    try:
        df = pd.read_csv(path)
    except Exception:
        try:
            df = pd.read_csv(path, sep=';', decimal=',')
        except Exception:
            # If both fail, create an empty dataframe to trigger defensive checks
            return pd.DataFrame()
    
    # Normalize column names
    df.columns = [" ".join(str(c).strip().split()) for c in df.columns]
    # Drop 'Unnamed' or ID-like columns
    cols_to_drop = [c for c in df.columns if 'unnamed' in c.lower() or c.lower() == 'id']
    df = df.drop(columns=cols_to_drop)
    return df

# Main Execution
dataset_path = 'Iris.csv'
df = load_data(dataset_path)

if df.empty:
    # Fallback for empty data
    print(f"ACCURACY={0.000000:.6f}")
    exit()

# Identify Target and Features
# Strategy: Look for 'species' or use the last column if not found
target_candidates = [c for c in df.columns if 'species' in c.lower() or 'target' in c.lower()]
if target_candidates:
    target_col = target_candidates[0]
else:
    target_col = df.columns[-1]

# Separate features and target
X = df.drop(columns=[target_col])
y = df[target_col]

# Preprocess Features: Coerce to numeric and handle NaNs
for col in X.columns:
    X.loc[:, col] = pd.to_numeric(X[col], errors='coerce')

# Drop rows where target is NaN or all features are NaN
df_cleaned = pd.concat([X, y], axis=1).dropna(subset=[target_col])
if df_cleaned.empty:
    print(f"ACCURACY={0.000000:.6f}")
    exit()

X = df_cleaned.drop(columns=[target_col])
y = df_cleaned[target_col]

# Ensure at least one numeric feature exists
X = X.select_dtypes(include=[np.number])
if X.empty:
    print(f"ACCURACY={0.000000:.6f}")
    exit()

# Impute remaining NaNs in features with 0
X = X.fillna(0)

# Encode Target if categorical
le = LabelEncoder()
y_encoded = le.fit_transform(y.astype(str))

