import pandas as pd
import numpy as np
import pickle
import os
from sklearn.model_selection import train_test_split
from sklearn.preprocessing import StandardScaler
from sklearn.neural_network import MLPClassifier
from sklearn.metrics import accuracy_score

def run_junction_classifier():
    target_file = '14k.csv' if os.path.exists('14k.csv') else 'input.csv'
    
    if not os.path.exists(target_file):
        return

    try:
        df = pd.read_csv(target_file, header=None, skiprows=1)
    except Exception:
        try:
            df = pd.read_csv(target_file, header=None, skiprows=1, sep=';', decimal=',')
        except Exception:
            return

    df = df.dropna(axis=1, how='all')
    
    data_array = df.to_numpy(dtype=np.float64)
    X = data_array[:, :-1]
    y_raw = data_array[:, -1]
    
    y = np.where(y_raw > 0, 1, np.where(y_raw < 0, -1, 0))

    X_train, X_test, y_train, y_test = train_test_split(
        X, y, test_size=0.3, random_state=42
    )

    scaler = StandardScaler()
    X_train = scaler.fit_transform(X_train)
    X_test = scaler.transform(X_test)

    mlp = None
    if os.path.exists("dict.pickle"):
        try:
            with open("dict.pickle", "rb") as f:
                mlp = pickle.load(f)
        except Exception:
            mlp = None

    if mlp is None:
        mlp = MLPClassifier(
            hidden_layer_sizes=(30, 30, 30, 30), 
            max_iter=1000, 
            random_state=42
        )
        mlp.fit(X_train, y_train)

    y_pred = mlp.predict(X_test)
    accuracy = accuracy_score(y_test, y_pred)
    
    print(f"ACCURACY={accuracy:.6f}")

if __name__ == "__main__":
    run_junction_classifier()

