import pandas as pd
import numpy as np
import sys
from sklearn.model_selection import train_test_split
from sklearn.preprocessing import StandardScaler, LabelEncoder
from sklearn.linear_model import LogisticRegression
from sklearn.pipeline import Pipeline
from sklearn.impute import SimpleImputer
from sklearn.metrics import accuracy_score

# Dataset configuration
DATA_PATH = "bodyPerformance.csv"

def load_data(path):
    # Robust CSV parsing
    try:
        df = pd.read_csv(path)
        if len(df.columns) <= 1:
            raise ValueError
    except:
        try:
            df = pd.read_csv(path, sep=';', decimal=',')
        except:
            # Final fallback
            return pd.DataFrame()
    
    # Normalize column names
    df.columns = [str(c).strip() for c in df.columns]
    df.columns = [" ".join(str(c).split()) for c in df.columns]
    df = df.loc[:, ~df.columns.str.contains('^Unnamed')]
    return df

def run_pipeline():
    df = load_data(DATA_PATH)
    
    if df.empty:
        print(f"ACCURACY={0.000000:.6f}")
        return

    # Identify Target
    # Based on DATASET_HEADERS, target is likely 'Blass' (or 'class' in typical versions)
    potential_targets = ['Blass', 'class', 'target', 'label', 'grade']
    target_col = None
    for pt in potential_targets:
        if pt in df.columns:
            target_col = pt
            break
    
    if target_col is None:
        # Fallback to last column
        target_col = df.columns[-1]

    # Pre-processing: Clean non-numeric data in features
    X = df.drop(columns=[target_col])
    y = df[target_col]

    # Identify numeric and categorical features
    numeric_features = X.select_dtypes(include=[np.number]).columns.tolist()
    
    # Force coercion of columns that should be numeric based on context
    for col in X.columns:
        if col not in numeric_features:
            converted = pd.to_numeric(X[col], errors='coerce')
            if not converted.isna().all():
                X[col] = converted
                numeric_features.append(col)

    # Use only numeric features for energy efficiency and simplicity in this baseline
    X = X[numeric_features]
    
    # Handle NaN and Infinity
    X = X.replace([np.inf, -np.inf], np.nan)
    
    # Target Encoding
    le = LabelEncoder()
    try:
        y_encoded = le.fit_transform(y.astype(str))
    except:
        # Trivial baseline if target processing fails
        print(f"ACCURACY={0.000000:.6f}")
        return

    # Check if we have valid data remaining
    if X.empty or len(y_encoded) == 0:
        print(f"ACCURACY={0.000000:.6f}")
        return

    # Train/Test Split
    X_train, X_test, y_train, y_test = train_test_split(
        X, y_encoded, test_size=0.2, random_state=42, stratify=y_encoded if len(np.unique(y_encoded)) > 1 else None
    )

    # Robust check for single-class targets
    if len(np.unique(y_train)) < 2:
        # If only one class is present, accuracy is trivial
        print(f"ACCURACY={1.000000:.6f}")
        return

    # Energy-efficient Pipeline: Simple Imputer + Scaler + Logistic Regression
    # Logistic Regression is preferred over ensembles for CPU efficiency and lower carbon footprint.
    pipeline = Pipeline([
        ('imputer', SimpleImputer(strategy='mean')),
        ('scaler', StandardScaler()),
        ('classifier', LogisticRegression(
            max_iter=1000, 
            multi_class='auto', 
            solver='lbfgs', 
            tol=1e-4,
            n_jobs=1 # Keeping it single-threaded to minimize overhead on small tasks
        ))
    ])

    # Model Training
    pipeline.fit(X_train, y_train)

    # Evaluation
    y_pred = pipeline.predict(X_test)
    accuracy = accuracy_score(y_test, y_pred)

    print(f"ACCURACY={accuracy:.6f}")

if __name__ == "__main__":
    run_pipeline()

