import sys
import pandas as pd
import numpy as np
from sklearn.model_selection import train_test_split
from sklearn.feature_extraction.text import TfidfVectorizer
from sklearn.linear_model import LogisticRegression
from sklearn.metrics import accuracy_score
from sklearn.preprocessing import LabelEncoder
from sklearn.pipeline import Pipeline

def run_pipeline():
    file_path = 'voting_data.csv'
    
    # Robust CSV parsing
    try:
        df = pd.read_csv(file_path, on_bad_lines='skip')
    except Exception:
        try:
            df = pd.read_csv(file_path, sep=';', decimal=',', on_bad_lines='skip')
        except Exception:
            # Fallback for extreme cases
            print("ACCURACY=0.000000")
            return

    # Normalize column names: strip, single space, drop Unnamed
    df.columns = [" ".join(str(c).strip().split()) for c in df.columns]
    df = df.loc[:, ~df.columns.str.contains('^Unnamed', case=False)]

    if df.empty:
        print("ACCURACY=0.000000")
        return

    # Schema derivation
    target_col = None
    # Prioritize 'label' as per DATASET_HEADERS, then common target names
    for candidate in ['label', 'target', 'class', 'category']:
        if candidate in df.columns:
            target_col = candidate
            break
    if target_col is None:
        target_col = df.columns[-1]

    feature_col = None
    # Prioritize 'text' as per DATASET_HEADERS, then common text names
    for candidate in ['text', 'sentence', 'body', 'content']:
        if candidate in df.columns and candidate != target_col:
            feature_col = candidate
            break
    if feature_col is None:
        available_features = [c for c in df.columns if c != target_col]
        if not available_features:
            print("ACCURACY=0.000000")
            return
        feature_col = available_features[0]

    # Data cleaning: handle NaNs and types
    df = df.dropna(subset=[target_col])
    if df.empty:
        print("ACCURACY=0.000000")
        return

    X_raw = df[feature_col].astype(str)
    y_raw = df[target_col].astype(str)

    # Label Encoding for classification
    le = LabelEncoder()
    y = le.fit_transform(y_raw)
    
    # Check if we have at least 2 classes for meaningful classification
    unique_classes = np.unique(y)
    if len(unique_classes) < 2:
        # Trivial baseline: if only one class exists, accuracy on any split is technically 1.0 or 0.0
        print("ACCURACY=1.000000")
        return

    # Train/Test Split
    try:
        X_train, X_test, y_train, y_test = train_test_split(
            X_raw, y, test_size=0.25, random_state=42, stratify=y if len(unique_classes) > 1 else None
        )
    except ValueError:
        # Fallback if stratify fails due to low class counts
        X_train, X_test, y_train, y_test = train_test_split(
            X_raw, y, test_size=0.25, random_state=42
        )

    if len(X_train) == 0 or len(X_test) == 0:
        print("ACCURACY=0.000000")
        return

    # Energy-efficient Pipeline:
    # 1. TF-IDF with restricted max_features reduces vocabulary size and memory footprint.
    # 2. LogisticRegression (liblinear) is very fast for high-dimensional sparse text data.
    pipeline = Pipeline([
        ('tfidf', TfidfVectorizer(
            max_features=2000, 
            stop_words='english', 
            ngram_range=(1, 1),
            sublinear_tf=True
        )),
        ('clf', LogisticRegression(
            max_iter=500, 
            solver='liblinear', 
            C=1.0, 
            random_state=42
        ))
    ])

    # Fit and Predict
    pipeline.fit(X_train, y_train)
    predictions = pipeline.predict(X_test)
    
    accuracy = accuracy_score(y_test, predictions)
    print(f"ACCURACY={accuracy:.6f}")

if __name__ == "__main__":
    run_pipeline()

