import pandas as pd
import numpy as np
from sklearn.model_selection import train_test_split
from sklearn.preprocessing import StandardScaler, LabelEncoder
from sklearn.linear_model import LogisticRegression
from sklearn.pipeline import Pipeline
from sklearn.metrics import accuracy_score

DATASET_PATH = "Iris.csv"

# Robust CSV loading
try:
    df = pd.read_csv(DATASET_PATH)
    if df.shape[1] < 3:
        df = pd.read_csv(DATASET_PATH, sep=';', decimal=',')
except Exception:
    df = pd.read_csv(DATASET_PATH, sep=';', decimal=',')

# Normalize column names
df.columns = df.columns.str.strip().str.replace(r'\s+', ' ', regex=True)
df = df[[c for c in df.columns if not c.startswith('Unnamed')]]

# Identify target and features
target_col = None
expected_target = 'Species'
if expected_target in df.columns:
    target_col = expected_target
else:
    for c in df.columns:
        if df[c].dtype == object and df[c].nunique() >= 2:
            target_col = c
            break

# Drop ID-like columns
drop_cols = []
for c in df.columns:
    if c.lower() in ('id',):
        drop_cols.append(c)

df = df.drop(columns=[d for d in drop_cols if d in df.columns and d != target_col])

assert target_col is not None and target_col in df.columns, "No suitable target column found"

# Separate features and target
feature_cols = [c for c in df.columns if c != target_col]
X = df[list(feature_cols)].copy()
y = df[target_col].copy()

# Coerce numeric features
for c in X.columns:
    if X[c].dtype == object:
        try:
            X[c] = pd.to_numeric(X[c], errors='coerce')
        except Exception:
            pass

# Drop any remaining non-numeric columns (keep it simple for this numeric dataset)
numeric_cols = X.select_dtypes(include=[np.number]).columns.tolist()
X = X[numeric_cols]

# Handle NaN/inf
X = X.replace([np.inf, -np.inf], np.nan)
X = X.fillna(X.median())

# Encode target
le = LabelEncoder()
y_encoded = le.fit_transform(y)

assert len(X) > 0, "Dataset empty after preprocessing"
assert len(np.unique(y_encoded)) >= 2, "Target has fewer than 2 classes"

# Train/test split
X_train, X_test, y_train, y_test = train_test_split(
    X.values, y_encoded, test_size=0.2, random_state=42, stratify=y_encoded
)

assert len(X_train) > 0 and len(X_test) > 0, "Train or test split is empty"

# Lightweight pipeline: StandardScaler + LogisticRegression
pipeline = Pipeline([
    ('scaler', StandardScaler()),
    ('clf', LogisticRegression(
        max_iter=200,
        solver='lbfgs',
        multi_class='multinomial',
        random_state=42
    ))
])

pipeline.fit(X_train, y_train)
y_pred = pipeline.predict(X_test)

accuracy = accuracy_score(y_test, y_pred)
print(f"ACCURACY={accuracy:.6f}")

