def _clean_columns(cols):
    cleaned = []
    for c in cols:
        c2 = re.sub(r"\s+", " ", str(c).strip())
        cleaned.append(c2)
    return cleaned


def _drop_unnamed_columns(df):
    unnamed = [c for c in df.columns if str(c).strip().lower().startswith("unnamed:")]
    if unnamed:
        df = df.drop(columns=unnamed, errors="ignore")
    return df


def _try_read_csv(path):
    # Robust parsing fallback: default, then ;/decimal=,
    try:
        df = pd.read_csv(path)
    except Exception:
        df = None

    def looks_wrong(d):
        if d is None or d.empty:
            return True
        # If only one column and it contains delimiter-like strings, likely wrong separator
        if d.shape[1] == 1:
            s = d.iloc[:, 0].astype(str).head(20).tolist()
            joined = " ".join(s)
            if (";" in joined) or ("," in joined):
                return True
        return False

    if looks_wrong(df):
        try:
            df2 = pd.read_csv(path, sep=";", decimal=",")
            if not looks_wrong(df2):
                df = df2
        except Exception:
            pass

    if df is None:
        raise RuntimeError("Could not read dataset.")
    return df


def _find_dataset_file():
    # Try common dataset file names first, then any CSV in project
    candidates = [
        "iris.csv",
        "Iris.csv",
        "IRIS.csv",
        "data.csv",
        "dataset.csv",
        "train.csv",
        "iris.data",
    ]
    for c in candidates:
        if os.path.isfile(c):
            return c

    csvs = sorted(glob.glob("**/*.csv", recursive=True))
    if csvs:
        return csvs[0]

    # Support iris.data (often comma-separated without header)
    datas = sorted(glob.glob("**/*.data", recursive=True))
    if datas:
        return datas[0]

    # Last resort: any file that looks like a dataset
    txts = sorted(glob.glob("**/*.txt", recursive=True))
    if txts:
        return txts[0]

    raise FileNotFoundError("No dataset file found (expected .csv/.data/.txt).")


def _read_any_dataset(path):
    ext = os.path.splitext(path)[1].lower()
    if ext == ".csv":
        return _try_read_csv(path)
    if ext in (".data", ".txt"):
        # Attempt as CSV-like without header
        try:
            df = pd.read_csv(path, header=None)
        except Exception:
            df = pd.read_csv(path, header=None, sep=";", decimal=",")
        return df
    return _try_read_csv(path)


def _normalize_df(df):
    df = df.copy()
    df.columns = _clean_columns(df.columns)
    df = _drop_unnamed_columns(df)
    # Collapse duplicate columns if any
    if len(set(df.columns)) != len(df.columns):
        # Keep first occurrence
        _, idx = np.unique(df.columns, return_index=True)
        df = df.iloc[:, np.sort(idx)]
    return df


def _coerce_numeric_cols(df, cols):
    for c in cols:
        df[c] = pd.to_numeric(df[c], errors="coerce")
    return df


def _pick_target_and_task(df):
    # Prefer an object/categorical column with low-ish cardinality as classification target,
    # else numeric regression target with variance.
    n_rows = len(df)
    if n_rows == 0 or df.shape[1] == 0:
        return None, None

    # Candidate classification targets: object/category/bool with >=2 classes, not too many unique values
    obj_cols = [c for c in df.columns if df[c].dtype == "object" or str(df[c].dtype).startswith("category") or df[c].dtype == "bool"]
    best_cls = None
    best_score = -1
    for c in obj_cols:
        s = df[c].dropna()
        if s.empty:
            continue
        nun = int(s.nunique())
        if nun < 2:
            continue
        # Heuristic: prefer moderate cardinality and sufficient coverage
        # Score: closer to sqrt(n) and penalize very high cardinality
        target = max(2, int(math.sqrt(n_rows)))
        score = -abs(nun - target)
        # Additional boost if looks like "species"/"class"/"target"/"label"
        name = str(c).lower()
        if any(k in name for k in ("species", "class", "target", "label", "y")):
            score += 3
        if score > best_score:
            best_score = score
            best_cls = c

    if best_cls is not None:
        return best_cls, "classification"

    # Candidate regression targets: numeric with variance and not too many missing
    numeric_cols = [c for c in df.columns if pd.api.types.is_numeric_dtype(df[c])]
    best_reg = None
    best_var = -1.0
    for c in numeric_cols:
        s = df[c]
        s = pd.to_numeric(s, errors="coerce")
        if s.notna().sum() < max(10, int(0.5 * n_rows)):
            continue
        v = float(np.nanvar(s.values))
        if v > best_var and np.isfinite(v) and v > 0:
            best_var = v
            best_reg = c

    if best_reg is not None:
        return best_reg, "regression"

    # Last resort: first column
    return df.columns[-1], "classification"


def _bounded_regression_score(y_true, y_pred):
    # Stable proxy in [0,1]: 1 / (1 + normalized MAE)
    y_true = np.asarray(y_true, dtype=float)
    y_pred = np.asarray(y_pred, dtype=float)
    mae = float(np.nanmean(np.abs(y_true - y_pred)))
    scale = float(np.nanstd(y_true))
    if not np.isfinite(scale) or scale <= 1e-12:
        scale = float(np.nanmean(np.abs(y_true))) if np.isfinite(np.nanmean(np.abs(y_true))) else 1.0
    denom = max(scale, 1e-12)
    score = 1.0 / (1.0 + (mae / denom))
    if not np.isfinite(score):
        score = 0.0
    return float(np.clip(score, 0.0, 1.0))


