def clean_columns(cols):
    cleaned = []
    seen = {}
    for c in cols:
        c = re.sub(r'\s+', ' ', str(c).strip())
        if c == '':
            c = 'col'
        if c in seen:
            seen[c] += 1
            c = f"{c}_{seen[c]}"
        else:
            seen[c] = 0
        cleaned.append(c)
    return cleaned

