import pandas as pd
import numpy as np
from sklearn.model_selection import train_test_split
from sklearn.preprocessing import StandardScaler, LabelEncoder
from sklearn.linear_model import LogisticRegression
from sklearn.impute import SimpleImputer
from sklearn.pipeline import Pipeline
from sklearn.compose import ColumnTransformer
from sklearn.metrics import accuracy_score

def load_data(path):
    try:
        df = pd.read_csv(path)
        if len(df.columns) <= 1:
            raise ValueError
    except Exception:
        df = pd.read_csv(path, sep=';', decimal=',')
    
    # Normalize column names
    df.columns = [str(c).strip() for c in df.columns]
    df.columns = [" ".join(str(c).split()) for c in df.columns]
    df = df.drop(columns=[c for c in df.columns if 'Unnamed:' in c], errors='ignore')
    return df

def run_pipeline(path):
    df = load_data(path)
    if df.empty:
        return 0.0

    # Identify target and features
    target_col = 'species'
    if target_col not in df.columns:
        # Fallback: assume last column is target
        target_col = df.columns[-1]
    
    X = df.drop(columns=[target_col])
    y = df[target_col]

    # Clean numeric data
    for col in X.columns:
        if X[col].dtype == 'object':
            try:
                X[col] = pd.to_numeric(X[col].str.replace(',', '.'), errors='coerce')
            except:
                pass

    # Select numeric and categorical features
    numeric_features = X.select_dtypes(include=[np.number]).columns.tolist()
    categorical_features = X.select_dtypes(exclude=[np.number]).columns.tolist()

    if not numeric_features and not categorical_features:
        return 0.0

    # Preprocessing
    numeric_transformer = Pipeline(steps=[
        ('imputer', SimpleImputer(strategy='median')),
        ('scaler', StandardScaler())
    ])

    # Simple pipeline to keep CPU usage low
    preprocessor = ColumnTransformer(
        transformers=[
            ('num', numeric_transformer, numeric_features)
        ],
        remainder='drop' # Drop categoricals for simplicity/speed in this baseline
    )

    # Encode target
    le = LabelEncoder()
    y_encoded = le.fit_transform(y.astype(str))
    
    # Check if we have enough classes for classification
    unique_classes = np.unique(y_encoded)
    if len(unique_classes) < 2:
        print(f"ACCURACY={1.000000:.6f}")
        return

    # Train/Test Split
    X_train, X_test, y_train, y_test = train_test_split(
        X, y_encoded, test_size=0.2, random_state=42, stratify=y_encoded if len(unique_classes) > 1 else None
    )

    # Model choice: Logistic Regression (CPU friendly, fast convergence)
    model = Pipeline(steps=[
        ('preprocessor', preprocessor),
        ('classifier', LogisticRegression(max_iter=1000, solver='lbfgs', multi_class='auto'))
    ])

    model.fit(X_train, y_train)
    y_pred = model.predict(X_test)
    
    accuracy = accuracy_score(y_test, y_pred)
    print(f"ACCURACY={accuracy:.6f}")

if __name__ == "__main__":
    run_pipeline('iris.csv')

# Optimization Summary
# 1. Model Choice: Logistic Regression was selected as it is computationally lightweight (O(n_features * n_samples)) compared to Ensembles or Deep Learning.
# 2. Solver: Used 'lbfgs' which is memory-efficient and converges quickly on small-to-medium datasets like Iris.
# 3. Preprocessing: Utilized a Scikit-Learn Pipeline to ensure efficient data flow and prevent redundant transformations.
# 4. Feature Selection: Focused on numeric features with Median Imputation and Standard Scaling to ensure stability without complex feature engineering.
# 5. Robustness: Implemented multi-stage CSV parsing and dynamic column selection to prevent execution failure on varying schemas.
# 6. Green Metric: Minimal iterations (max_iter=1000) and no hyperparameter grid search significantly reduces the carbon footprint of the training phase.
# 7. Data Handling: Used 'coerce' on numeric conversion to handle dirty data gracefully without manual intervention.