import pandas as pd
import numpy as np
from sklearn.preprocessing import StandardScaler
from sklearn.neighbors import KNeighborsClassifier
from sklearn.metrics import accuracy_score

def load_csv(path: str) -> pd.DataFrame:
    return pd.read_csv(path)

def main() -> None:
    # Load the provided training and test splits
    train_df = load_csv('Data/Diabetes-Training.csv')
    test_df = load_csv('Data/Diabetes-Clasification.csv')

    # Separate features and target
    X_train = train_df.drop(columns=['class'])
    y_train = train_df['class']
    X_test = test_df.drop(columns=['class'])
    y_test = test_df['class']

    # Standardise features – simple linear scaling (mean 0, std 1)
    scaler = StandardScaler()
    X_train_scaled = scaler.fit_transform(X_train)
    X_test_scaled = scaler.transform(X_test)

    # Lightweight nearest‑neighbour classifier (k=3 chosen for a balance of bias‑variance)
    knn = KNeighborsClassifier(n_neighbors=3, metric='euclidean')
    knn.fit(X_train_scaled, y_train)

    # Predict and evaluate
    y_pred = knn.predict(X_test_scaled)
    accuracy = accuracy_score(y_test, y_pred)

    # Only the required accuracy output
    print(f"ACCURACY={accuracy:.6f}")

if __name__ == "__main__":
    main()