from scipy.io import arff
import pandas as pd
from sklearn.model_selection import train_test_split
from sklearn.ensemble import RandomForestClassifier
from sklearn.metrics import accuracy_score
import joblib

# Load ARFF data
data, _ = arff.loadarff("dataset_adult.arff")
df = pd.DataFrame(data)

# Decode byte strings for categorical columns
for col in df.select_dtypes([object]).columns:
    df[col] = df[col].str.decode('utf-8')

# One‑hot encode categorical features
df_enc = pd.get_dummies(df, drop_first=True)

# Separate features and target
X = df_enc.drop('income_>50K', axis=1)
y = df_enc['income_>50K']

# Train‑test split
X_train, X_test, y_train, y_test = train_test_split(
    X, y, test_size=0.3, random_state=42
)

# Lightweight Random Forest (few trees, shallow depth)
clf = RandomForestClassifier(
    n_estimators=50,
    max_depth=10,
    random_state=42,
    n_jobs=-1
)
clf.fit(X_train, y_train)

# Evaluate accuracy
y_pred = clf.predict(X_test)
accuracy = accuracy_score(y_test, y_pred)
print(f"ACCURACY={accuracy:.6f}")

# Persist the trained model
joblib.dump(clf, "adult_rf_model.joblib")