import glob
import pandas as pd
from sklearn.model_selection import train_test_split
from sklearn.preprocessing import LabelEncoder
from sklearn.feature_extraction.text import CountVectorizer
from sklearn.naive_bayes import MultinomialNB
from sklearn.pipeline import Pipeline
from sklearn.metrics import accuracy_score

def locate_dataset():
    files = glob.glob("*.csv")
    if not files:
        files = glob.glob("**/*.csv", recursive=True)
    if not files:
        raise FileNotFoundError("No CSV dataset found.")
    return files[0]

path = locate_dataset()
try:
    data = pd.read_csv(path)
except Exception:
    data = pd.read_csv(path, sep="\t", header=None)

if "Category" not in data.columns or "Message" not in data.columns:
    if data.shape[1] >= 2:
        data = data.iloc[:, :2]
        data.columns = ["Category", "Message"]
    else:
        raise ValueError("Dataset must contain at least two columns.")

data = data.dropna(subset=["Category", "Message"])
data["Category"] = data["Category"].astype(str)
data["Message"] = data["Message"].astype(str)

labeler = LabelEncoder()
y = labeler.fit_transform(data["Category"])
X = data["Message"]

pipeline = Pipeline([
    ("vect", CountVectorizer(lowercase=True, stop_words="english", max_features=5000, token_pattern=r"\b\w+\b")),
    ("clf", MultinomialNB())
])

n_samples = len(data)
if n_samples < 2:
    pipeline.fit(X, y)
    preds = pipeline.predict(X)
    accuracy = accuracy_score(y, preds)
else:
    test_size = 1 if n_samples < 5 else 0.2
    strat = y if len(set(y)) > 1 and isinstance(test_size, float) else None
    X_train, X_test, y_train, y_test = train_test_split(X, y, test_size=test_size, random_state=42, stratify=strat)
    pipeline.fit(X_train, y_train)
    preds = pipeline.predict(X_test)
    accuracy = accuracy_score(y_test, preds)

print(f"ACCURACY={accuracy:.6f}")

# OPTIMIZATION SUMMARY
# - Limited vocabulary size in CountVectorizer to reduce memory and CPU usage.
# - MultinomialNB chosen for fast training and inference on sparse text data.
# - Simple train/test split avoids expensive cross-validation.
# - Pipeline keeps preprocessing minimal and reproducible on CPU.