import os
import pandas as pd
import numpy as np
from sklearn.model_selection import train_test_split
from sklearn.pipeline import Pipeline
from sklearn.impute import SimpleImputer
from sklearn.preprocessing import StandardScaler
from sklearn.linear_model import LogisticRegression
from sklearn.metrics import accuracy_score

def find_csv_file():
    preferred = ["data.csv", "dataset.csv", "train.csv", "admission.csv"]
    for name in preferred:
        if os.path.isfile(name):
            return name
    csvs = [f for f in os.listdir(".") if f.lower().endswith(".csv")]
    if not csvs:
        raise FileNotFoundError("No CSV dataset found.")
    return sorted(csvs)[0]

file_path = find_csv_file()
data = pd.read_csv(file_path)
data.columns = [c.strip().lower() for c in data.columns]

target = "admit"
if target not in data.columns:
    raise ValueError("Target column not found.")

data = data.dropna(subset=[target])
y = data[target]

if y.dtype == object or str(y.dtype).startswith("category"):
    y, _ = pd.factorize(y)

feature_cols = [c for c in ["gre", "gpa", "rank"] if c in data.columns]
if not feature_cols:
    feature_cols = [c for c in data.columns if c != target]

X = data[feature_cols].copy()
for c in X.columns:
    X[c] = pd.to_numeric(X[c], errors="coerce")

if len(y) < 2 or pd.Series(y).nunique() < 2:
    accuracy = 1.0
    print(f"ACCURACY={accuracy:.6f}")
else:
    test_size = 0.2 if len(y) >= 5 else 0.5
    class_counts = pd.Series(y).value_counts()
    stratify = y if class_counts.min() >= 2 else None
    X_train, X_test, y_train, y_test = train_test_split(
        X, y, test_size=test_size, random_state=42, stratify=stratify
    )

    model = Pipeline(
        steps=[
            ("imputer", SimpleImputer(strategy="median")),
            ("scaler", StandardScaler()),
            ("classifier", LogisticRegression(solver="liblinear", max_iter=100)),
        ]
    )
    model.fit(X_train, y_train)
    preds = model.predict(X_test)
    accuracy = accuracy_score(y_test, preds)
    print(f"ACCURACY={accuracy:.6f}")

