from sklearn.model_selection import train_test_split
from sklearn.preprocessing import StandardScaler
import tensorflow as tf
from tensorflow.keras.models import Sequential
from tensorflow.keras.layers import Dense
import os

os.environ['TF_CPP_MIN_LOG_LEVEL'] = '3'
tf.random.set_seed(0)
np.random.seed(0)

features = ['age', 'anaemia', 'creatinine_phosphokinase', 'diabetes', 'ejection_fraction', 
            'high_blood_pressure', 'platelets', 'serum_creatinine', 'serum_sodium', 'sex', 'smoking', 'time']
target = 'death_event'

data = pd.read_csv('heart_failure.csv', usecols=features + [target])
X = data[features].values.astype(np.float32)
y = data[target].values.astype(np.float32)

X_train, X_test, y_train, y_test = train_test_split(X, y, test_size=0.3, random_state=0)

scale_idx = [0, 2, 4, 6, 7, 8, 11]
scaler = StandardScaler()
X_train[:, scale_idx] = scaler.fit_transform(X_train[:, scale_idx])
X_test[:, scale_idx] = scaler.transform(X_test[:, scale_idx])

model = Sequential([
    Dense(12, activation='relu', input_shape=(12,)),
    Dense(1, activation='sigmoid')
])

model.compile(loss='binary_crossentropy', optimizer='adam', metrics=['accuracy'])

model.fit(X_train, y_train, epochs=100, batch_size=16, verbose=0)

_, accuracy = model.evaluate(X_test, y_test, verbose=0)

