import pandas as pd
import numpy as np
import tensorflow as tf
from sklearn.preprocessing import MinMaxScaler
from sklearn.decomposition import PCA
from sklearn.ensemble import IsolationForest
from sklearn.metrics import accuracy_score
from tensorflow.keras.models import Sequential
from tensorflow.keras.layers import BatchNormalization, Dense

tf.random.set_seed(1)
np.random.seed(100)

# Load datasets
train_df = pd.read_csv("herg_train_activity.csv")
test_df = pd.read_csv("herg_test_activity.csv")

X_train = train_df.drop('Activity_value', axis=1).values
y_train = train_df[['Activity_value']].values.ravel()
X_test = test_df.drop('Activity_value', axis=1).values
y_test = test_df[['Activity_value']].values.ravel()

# Scale features
scaler = MinMaxScaler()
X_train = scaler.fit_transform(X_train)
X_test = scaler.transform(X_test)

# Outlier removal with PCA + IsolationForest
pca = PCA(n_components=2, random_state=0)
X_train_pca = pca.fit_transform(X_train)
X_test_pca = pca.transform(X_test)

iso = IsolationForest(contamination=0.1, n_estimators=100, random_state=0, verbose=0)
iso.fit(X_train_pca)

mask_train = iso.predict(X_train_pca) != -1
X_train, y_train = X_train[mask_train], y_train[mask_train]

mask_test = iso.predict(X_test_pca) != -1
X_test, y_test = X_test[mask_test], y_test[mask_test]

# Build neural network
model = Sequential([
    BatchNormalization(input_shape=(8,)),
    Dense(200, activation='relu', kernel_initializer='random_uniform', kernel_constraint='maxnorm'),
    BatchNormalization(),
    Dense(200, activation='relu'),
    Dense(200, activation='relu'),
    Dense(1, activation='sigmoid')
])
model.compile(loss='binary_crossentropy', optimizer='adam', metrics=['accuracy'])

# Train
model.fit(X_train, y_train,
          epochs=200,
          batch_size=100,
          shuffle=True,
          verbose=0,
          validation_data=(X_test, y_test))

# Evaluate
_, test_acc = model.evaluate(X_test, y_test, verbose=0)
print(f"ACCURACY={test_acc:.6f}")

# Predict on new data and save
X_new = pd.read_csv("cas.csv").values
X_new_scaled = scaler.transform(X_new)
pred = model.predict(X_new_scaled)
pred_class = (pred > 0.5).astype(int)
pd.DataFrame(pred_class, columns=['prediction']).to_csv('CAS_full_herg.csv', index=False)