import pandas as pd
import numpy as np
import random
from sklearn.model_selection import train_test_split, RandomizedSearchCV
from sklearn.preprocessing import MinMaxScaler
from sklearn.svm import SVC
from sklearn.metrics import accuracy_score
from imblearn.over_sampling import SMOTE
import scipy.stats as stats

SEED = 42
np.random.seed(SEED)
random.seed(SEED)

DATASET_PATH = "diabetes2.csv"
DATASET_HEADERS = [
    "Pregnancies",
    "Glucose",
    "BloodPressure",
    "SkinThickness",
    "Insulin",
    "BMI",
    "DiabetesPedigreeFunction",
    "Age",
    "Outcome",
]

def load_dataset(path, headers):
    expected = [h.strip() for h in headers]
    expected_lower = [h.lower() for h in expected]

    def normalize(df):
        df.columns = [c.strip() for c in df.columns]
        return df

    def has_expected(df):
        cols_lower = [c.lower() for c in df.columns]
        return set(expected_lower).issubset(cols_lower)

    df = normalize(pd.read_csv(path))
    if df.shape[1] == 1 or not has_expected(df):
        df_alt = normalize(pd.read_csv(path, sep=";", decimal=","))
        if df_alt.shape[1] > 1:
            df = df_alt
    if df.shape[1] == len(expected) and not has_expected(df):
        df.columns = expected
    cols_lower = [c.lower() for c in df.columns]
    if set(expected_lower).issubset(cols_lower):
        mapping = {c.lower(): c for c in df.columns}
        df = df[[mapping[h] for h in expected_lower]]
    return df

def main():
    data = load_dataset(DATASET_PATH, DATASET_HEADERS)
    target_key = DATASET_HEADERS[-1].lower()
    lower_map = {c.lower(): c for c in data.columns}
    target_col = lower_map.get(target_key, data.columns[-1])

    X = data.drop(columns=[target_col])
    y = data[target_col]

    X_train, X_test, y_train, y_test = train_test_split(
        X, y, test_size=0.3, random_state=SEED
    )

    scaler = MinMaxScaler()
    X_train_scaled = scaler.fit_transform(X_train)
    X_test_scaled = scaler.transform(X_test)

    smote = SMOTE(random_state=SEED)
    X_resampled, y_resampled = smote.fit_resample(X_train_scaled, y_train)

    param_distributions = {
        "C": stats.loguniform(1e-3, 1e3),
        "gamma": stats.loguniform(1e-3, 1e3),
        "kernel": ["linear", "rbf"],
    }

    random_search = RandomizedSearchCV(
        SVC(),
        param_distributions,
        n_iter=50,
        cv=5,
        n_jobs=-1,
        random_state=SEED,
    )

    random_search.fit(X_resampled, y_resampled)

    best_model = random_search.best_estimator_
    y_pred = best_model.predict(X_test_scaled)
    accuracy = accuracy_score(y_test, y_pred)

    print(f"ACCURACY={accuracy:.6f}")

if __name__ == "__main__":
    main()

