"""
Energy‑efficient ML pipeline for predicting student graduation.

Design choices:
- Minimal preprocessing: one imputer for numeric and categorical values, OrdinalEncoder for categoricals to keep dimensionality low.
- Light model: RandomForestClassifier with only 50 trees and depth 10; this keeps inference fast and memory usage modest while still capturing interactions.
- CPU‑only execution; n_jobs=1 prevents multi‑thread overhead.
- Only a single accuracy print as required.
"""

import pandas as pd
from sklearn.model_selection import train_test_split
from sklearn.compose import ColumnTransformer
from sklearn.preprocessing import OrdinalEncoder
from sklearn.impute import SimpleImputer
from sklearn.pipeline import Pipeline
from sklearn.metrics import accuracy_score
from sklearn.ensemble import RandomForestClassifier
from sklearn.preprocessing import LabelEncoder

# Load data
df = pd.read_csv('data/students_graduate_predict.csv', delimiter=';')

# Separate features and target
X = df.drop(columns=['Graduated (target)'])
y = df['Graduated (target)']

# Encode target if not numeric
if y.dtype == object:
    le = LabelEncoder()
    y = le.fit_transform(y)

# Identify numeric and categorical columns
numeric_cols = X.select_dtypes(exclude='object').columns.tolist()
categorical_cols = X.select_dtypes(include='object').columns.tolist()

# Preprocessing pipelines
numeric_pipeline = Pipeline(steps=[
    ('imputer', SimpleImputer(strategy='median'))
])

categorical_pipeline = Pipeline(steps=[
    ('imputer', SimpleImputer(strategy='most_frequent')),
    ('encoder', OrdinalEncoder(handle_unknown='use_encoded_value', unknown_value=-1))
])

preprocessor = ColumnTransformer(
    transformers=[
        ('num', numeric_pipeline, numeric_cols),
        ('cat', categorical_pipeline, categorical_cols)
    ])

# Model
model = RandomForestClassifier(
    n_estimators=50,
    max_depth=10,
    random_state=1,
    class_weight='balanced',
    n_jobs=1
)

# Full pipeline
clf = Pipeline(steps=[
    ('preprocessor', preprocessor),
    ('classifier', model)
])

# Train-test split
X_train, X_test, y_train, y_test = train_test_split(
    X, y,
    test_size=0.15,
    random_state=1,
    stratify=y
)

# Fit
clf.fit(X_train, y_train)

# Predict
y_pred = clf.predict(X_test)

# Evaluate
accuracy = accuracy_score(y_test, y_pred)

print(f"ACCURACY={accuracy:.6f}")