def determine_task(y):
    y_non_null = y.dropna()
    if y_non_null.empty:
        return "regression"
    if pd.api.types.is_bool_dtype(y_non_null):
        return "classification"
    if pd.api.types.is_object_dtype(y_non_null) or pd.api.types.is_categorical_dtype(y_non_null):
        y_num = pd.to_numeric(y_non_null, errors="coerce")
