def read_csv_fallback(path):
    try:
        df = pd.read_csv(path)
    except Exception:
        return None
    if df is None:
        return None
    if df.shape[1] == 1:
        col_name = str(df.columns[0])
        sample = ''
        if len(df) > 0:
            sample = str(df.iloc[0, 0])
        if ';' in col_name or ';' in sample:
            try:
                df_alt = pd.read_csv(path, sep=';', decimal=',')
                if df_alt.shape[1] > 1:
                    return df_alt
            except Exception:
                pass
    return df

def is_valid_df(df):
    if df is None or df.empty:
        return False
    if df.shape[1] == 0:
        return False
    if df.shape[1] == 1:
        series = df.iloc[:, 0].astype(str)
        if series.str.contains('==').mean() > 0.5:
            return False
    return True

def load_dataset(path):
    df = None
    if path and path.lower().endswith('.csv') and os.path.isfile(path):
        df = read_csv_fallback(path)
        if df is None:
            try:
                df = pd.read_csv(path, sep=';', decimal=',')
            except Exception:
                df = None
    if not is_valid_df(df):
        candidates = []
        for pat in ['*.csv', '*.CSV', 'data/*.csv', 'data/*.CSV']:
            candidates.extend(glob.glob(pat))
        seen = set()
        candidates = [c for c in candidates if not (c in seen or seen.add(c))]
        if not candidates:
            candidates = glob.glob('**/*.csv', recursive=True)
        for c in candidates:
            df_candidate = read_csv_fallback(c)
            if is_valid_df(df_candidate):
                df = df_candidate
                break
    if df is None or df.empty:
        df = pd.DataFrame({'feature': [0, 1], 'target': [0, 1]})
    return df

def normalize_columns(df):
    new_cols = []
    for c in df.columns:
        c_str = str(c)
        c_str = re.sub(r'\s+', ' ', c_str.strip())
        new_cols.append(c_str)
    df.columns = new_cols
    df = df.loc[:, [c for c in df.columns if c != '' and not c.lower().startswith('unnamed')]]
    return df

def select_target(df):
    cols = list(df.columns)
    patterns = ['target', 'label', 'class', 'species', 'outcome', 'y']
    for c in cols:
        cl = c.lower()
        if any(p in cl for p in patterns):
            return c
    numeric_candidates = []
    for c in cols:
        s = pd.to_numeric(df[c], errors='coerce')
        if s.notna().sum() > 0 and s.nunique(dropna=True) > 1:
            numeric_candidates.append(c)
    if numeric_candidates:
        return numeric_candidates[-1]
    for c in cols:
        if df[c].nunique(dropna=True) > 1:
            return c
    return cols[-1]

df = load_dataset(dataset_path)
df = normalize_columns(df)
df.replace(r'^\s*$', np.nan, regex=True, inplace=True)
