import os
import numpy as np
import pandas as pd

from sklearn.model_selection import train_test_split
from sklearn.preprocessing import StandardScaler, LabelEncoder
from sklearn.pipeline import Pipeline
from sklearn.linear_model import LogisticRegression
from sklearn.metrics import accuracy_score


RANDOM_SEED = 42
DATASET_PATH = "Iris.csv"
DATASET_HEADERS = ["Id", "SepalLengthCm", "SepalWidthCm", "PetalLengthCm", "PetalWidthCm", "Species"]


def _read_csv_with_fallback(path: str, expected_headers: list[str]) -> pd.DataFrame:
    df = pd.read_csv(path)
    looks_wrong = (df.shape[1] <= 1) or (not any(col in df.columns for col in expected_headers))
    if looks_wrong:
        df = pd.read_csv(path, sep=";", decimal=",")
    return df


def _prepare_data(df: pd.DataFrame) -> tuple[np.ndarray, np.ndarray]:
    if "Id" in df.columns:
        df = df.drop(columns=["Id"])

    if "Species" not in df.columns:
        raise ValueError(f"Target column not found. Available columns: {list(df.columns)}")

    for col in df.columns:
        if col != "Species":
            df[col] = pd.to_numeric(df[col], errors="coerce")

    df = df.dropna(axis=0, how="any")

    y_raw = df["Species"].astype(str).to_numpy()
    X = df.drop(columns=["Species"]).to_numpy(dtype=np.float64, copy=False)

    le = LabelEncoder()
    y = le.fit_transform(y_raw)

    return X, y


def main() -> None:
    np.random.seed(RANDOM_SEED)

    df = _read_csv_with_fallback(DATASET_PATH, DATASET_HEADERS)
    X, y = _prepare_data(df)

    X_train, X_test, y_train, y_test = train_test_split(
        X,
        y,
        test_size=0.2,
        random_state=RANDOM_SEED,
        stratify=y,
    )

    pipeline = Pipeline(
        steps=[
            ("scaler", StandardScaler()),
            ("model", LogisticRegression(max_iter=200, random_state=RANDOM_SEED, n_jobs=1)),
        ]
    )

    pipeline.fit(X_train, y_train)
    y_pred = pipeline.predict(X_test)
    accuracy = accuracy_score(y_test, y_pred)

    print(f"ACCURACY={accuracy:.6f}")


if __name__ == "__main__":
    main()

