from sklearn.linear_model import LogisticRegression
from sklearn.metrics import accuracy_score

np.random.seed(42)

try:
    df = pd.read_csv("Iris.csv")
    if df.shape[1] < 3:
        df = pd.read_csv("Iris.csv", sep=";", decimal=",")
except Exception:
    df = pd.read_csv("Iris.csv", sep=";", decimal=",")

id_cols = [c for c in df.columns if c.lower() == "id"]
