try:
    df = pd.read_csv("Iris.csv")
    if df.shape[1] < 3:
        df = pd.read_csv("Iris.csv", sep=";", decimal=",")
except Exception:
    df = pd.read_csv("Iris.csv", sep=";", decimal=",")

if "Id" in df.columns:
    df.drop(columns=["Id"], inplace=True)

le = LabelEncoder()
