import os
import pandas as pd
import numpy as np
from sklearn.model_selection import train_test_split
from sklearn.pipeline import Pipeline
from sklearn.impute import SimpleImputer
from sklearn.naive_bayes import GaussianNB
from sklearn.preprocessing import LabelEncoder
from sklearn.metrics import accuracy_score

EXPECTED_COLUMNS = ["sepal_length", "sepal_width", "petal_length", "petal_width", "species"]

def normalize_columns(columns):
    return [str(col).strip().lower().replace(" ", "_") for col in columns]

def load_dataset(expected_columns, max_depth=2):
    base = os.path.abspath(".")
    for root, dirs, files in os.walk(base):
        depth = root[len(base):].count(os.sep)
        if depth > max_depth:
            dirs[:] = []
            continue
        for filename in files:
            if filename.lower().endswith(".csv"):
                path = os.path.join(root, filename)
                try:
                    header_df = pd.read_csv(path, nrows=0)
                except Exception:
                    continue
                normalized = normalize_columns(header_df.columns)
                if all(col in normalized for col in expected_columns):
                    data = pd.read_csv(path)
                    data.columns = normalize_columns(data.columns)
                    return data
    raise FileNotFoundError("Dataset with expected headers not found.")

df = load_dataset(EXPECTED_COLUMNS)
df = df[EXPECTED_COLUMNS]
df = df.dropna(subset=["species"])

X = df.drop(columns=["species"])
y = df["species"]

label_encoder = LabelEncoder()
y_encoded = label_encoder.fit_transform(y)

stratify_target = y_encoded if len(np.unique(y_encoded)) > 1 else None

X_train, X_test, y_train, y_test = train_test_split(
    X, y_encoded, test_size=0.2, random_state=42, stratify=stratify_target
)

model = Pipeline(steps=[
    ("imputer", SimpleImputer(strategy="median")),
    ("classifier", GaussianNB())
])

model.fit(X_train, y_train)
predictions = model.predict(X_test)
accuracy = accuracy_score(y_test, predictions)
print(f"ACCURACY={accuracy:.6f}")

# OPTIMIZATION SUMMARY
# Used GaussianNB for fast, low-memory training suitable for small tabular data.
# Applied a lightweight median imputer within a pipeline to ensure reproducible preprocessing.
# Limited filesystem search depth to reduce unnecessary I/O overhead.