import pandas as pd
import numpy as np
import warnings
import os
from sklearn.model_selection import train_test_split
from sklearn.pipeline import Pipeline
from sklearn.compose import ColumnTransformer
from sklearn.preprocessing import StandardScaler, OneHotEncoder
from sklearn.impute import SimpleImputer
from sklearn.linear_model import LogisticRegression, Ridge
from sklearn.metrics import accuracy_score

warnings.filterwarnings("ignore")

def run_pipeline():
    # Use the provided dataset path
    dataset_path = 'model.pkl'
    
    # 1. ROBUST DATA LOADING
    df = None
    if not os.path.exists(dataset_path):
        # Fallback for environment where file might be named differently or missing
        print(f"ACCURACY={0.0:.6f}")
        return

    try:
        # Priority 1: Try pickle as extension suggests
        df = pd.read_pickle(dataset_path)
    except Exception:
        try:
            # Priority 2: Standard CSV
            df = pd.read_csv(dataset_path)
            if df.shape[1] <= 1:
                raise ValueError
        except Exception:
            try:
                # Priority 3: Alternative CSV format
                df = pd.read_csv(dataset_path, sep=';', decimal=',')
            except Exception:
                df = pd.DataFrame()

    if df is None or df.empty:
        print(f"ACCURACY={0.0:.6f}")
        return

    # 2. SCHEMA NORMALIZATION
    # Strip whitespace and normalize column headers
    df.columns = [" ".join(str(c).strip().split()) for c in df.columns]
    # Remove Unnamed columns
    df = df.loc[:, ~df.columns.str.contains('^Unnamed')]
    
    if df.shape[1] < 2:
        print(f"ACCURACY={0.0:.6f}")
        return

    # 3. TARGET SELECTION
    # Heuristic: Choose the last column as the target, excluding common ID-like columns
    potential_targets = [c for c in df.columns if not c.lower().startswith('id')]
    target_col = potential_targets[-1] if potential_targets else df.columns[-1]
    
    # Clean target: drop nulls in target
    df = df.dropna(subset=[target_col])
    if df.empty:
        print(f"ACCURACY={0.0:.6f}")
        return

    y = df[target_col]
    X = df.drop(columns=[target_col])
    
    # 4. ROBUST FEATURE COERCION
    # Try to convert object columns to numeric if possible (handles dirty numeric data)
    for col in X.columns:
        if X[col].dtype == 'object':
            try:
                converted = pd.to_numeric(X[col], errors='coerce')
                if not converted.isna().all():
                    X[col] = converted
            except:
                pass

    # Separate feature types
    numeric_features = X.select_dtypes(include=['int64', 'float64', 'bool']).columns.tolist()
    categorical_features = X.select_dtypes(exclude=['int64', 'float64', 'bool']).columns.tolist()

    # 5. TASK DETECTION (Classification vs Regression)
    is_classification = True
    unique_y = y.nunique()
    if y.dtype == 'float64' and unique_y > 20:
        is_classification = False
    elif y.dtype == 'object' or y.dtype == 'bool' or unique_y <= 20:
        is_classification = True
    else:
        is_classification = False

    # 6. ENERGY-EFFICIENT PREPROCESSING PIPELINE
    # Numeric: Median imputation and scaling for linear models
    num_transformer = Pipeline(steps=[
        ('imputer', SimpleImputer(strategy='median')),
        ('scaler', StandardScaler())
    ])
    
    # Categorical: Constant imputation and memory-efficient OneHot
    cat_transformer = Pipeline(steps=[
        ('imputer', SimpleImputer(strategy='constant', fill_value='missing')),
        ('onehot', OneHotEncoder(handle_unknown='ignore', sparse_output=True))
    ])

    transformers = []
    if numeric_features:
        transformers.append(('num', num_transformer, numeric_features))
    if categorical_features:
        transformers.append(('cat', cat_transformer, categorical_features))
            
    if not transformers:
        print(f"ACCURACY={0.0:.6f}")
        return
            
    preprocessor = ColumnTransformer(transformers=transformers)

    # 7. MODEL TRAINING & EVALUATION
    try:
        # Handle cases with very few samples
        test_size = 0.2 if len(df) > 5 else 0.5
        X_train, X_test, y_train, y_test = train_test_split(X, y, test_size=test_size, random_state=42)
        
        if is_classification:
            # Check for trivial class count
            if y_train.nunique() < 2:
                # If only one class exists in train, accuracy is measured by how many match in test
                y_pred = [y_train.iloc[0]] * len(y_test)
                accuracy = accuracy_score(y_test, y_pred) if len(y_test) > 0 else 1.0
            else:
                # Lightweight Logistic Regression
                clf = Pipeline(steps=[
                    ('preprocessor', preprocessor),
                    ('classifier', LogisticRegression(max_iter=500, solver='lbfgs', multi_class='auto', random_state=42))
                ])
                clf.fit(X_train, y_train)
                accuracy = clf.score(X_test, y_test)
        else:
            # Lightweight Ridge Regression
            reg = Pipeline(steps=[
                ('preprocessor', preprocessor),
                ('regressor', Ridge(random_state=42))
            ])
            reg.fit(X_train, y_train)
            r2 = reg.score(X_test, y_test)
            # Clip R2 to [0,1] range to serve as a stable accuracy proxy
            accuracy = max(0.0, min(1.0, r2))
    except Exception:
        accuracy = 0.0

    # FINAL OUTPUT
    print(f"ACCURACY={accuracy:.6f}")

if __name__ == "__main__":
    run_pipeline()

# Optimization Summary:
# 1. Energy-Efficient Algorithms: Used Logistic Regression and Ridge Regression, which minimize CPU cycles compared to Gradient Boosted Trees or Deep Learning.
# 2. CPU-Friendly Pipeline: Utilized scikit-learn's Pipeline and ColumnTransformer to ensure data is processed in a single pass without redundant copies.
# 3. Sparse Representations: OneHotEncoder configured with sparse_output=True (default) to minimize memory footprint during categorical transformation.
# 4. Robust Schema Logic: Implemented dynamic column type detection and multi-format CSV/Pickle loading to prevent runtime failures on unknown datasets.
# 5. Resource Conservations: Limited maximum iterations on the solver and used simple imputation strategies to maintain a low thermal design power (TDP) profile.
# 6. Regression Proxy: For continuous targets, the R-squared metric is clipped to the [0, 1] range to provide a consistent accuracy-like benchmark.