import pandas as pd
import numpy as np
from sklearn.model_selection import train_test_split
from sklearn.preprocessing import StandardScaler, OneHotEncoder, LabelEncoder
from sklearn.impute import SimpleImputer
from sklearn.compose import ColumnTransformer
from sklearn.pipeline import Pipeline
from sklearn.linear_model import LogisticRegression
from sklearn.metrics import accuracy_score

def run_pipeline(data_path):
    # Robust CSV loading
    try:
        df = pd.read_csv(data_path, sep=None, engine='python')
    except Exception:
        try:
            df = pd.read_csv(data_path, sep=';', decimal=',')
        except Exception:
            # Fallback to empty if catastrophic failure, though unlikely with standard data
            return 0.0

    # Normalize column names: strip, single spaces, remove Unnamed
    df.columns = [str(c).strip() for c in df.columns]
    df.columns = [" ".join(str(c).split()) for c in df.columns]
    df = df.loc[:, ~df.columns.str.contains('^Unnamed')]

    if df.empty:
        return 0.0

    # Schema derivation: identify target and features
    # Priority: 'style' (classification), then 'quality' (ordinal classification)
    target_col = None
    for cand in ['style', 'quality', 'target', 'class', 'label']:
        if cand in df.columns:
            target_col = cand
            break
    if target_col is None:
        target_col = df.columns[-1]

    # Pre-clean target: drop rows where target is NaN
    df = df.dropna(subset=[target_col])
    
    # Separate Features and Target
    X = df.drop(columns=[target_col])
    y = df[target_col]

    # Identify feature types
    numeric_features = []
    categorical_features = []
    
    for col in X.columns:
        # Coerce numeric to check if it's truly numeric
        converted = pd.to_numeric(X[col], errors='coerce')
        if pd.api.types.is_numeric_dtype(converted) and not pd.api.types.is_bool_dtype(X[col]):
            X.loc[:, col] = converted
            numeric_features.append(col)
        else:
            categorical_features.append(col)

    # Clean numeric: handle inf
    X[numeric_features] = X[numeric_features].replace([np.inf, -np.inf], np.nan)

    # Ensure dataset is not empty after cleaning
    if X.empty or len(y) < 10:
        return 0.0

    # Encode target if categorical
    le = LabelEncoder()
    y_encoded = le.fit_transform(y.astype(str))
    
    # Check for trivial class count
    if len(np.unique(y_encoded)) < 2:
        # Fallback accuracy for single-class scenario
        print(f"ACCURACY={1.000000:.6f}")
        return

    # Split
    X_train, X_test, y_train, y_test = train_test_split(
        X, y_encoded, test_size=0.2, random_state=42, stratify=y_encoded if len(np.unique(y_encoded)) > 1 else None
    )

    # Preprocessing Pipeline
    numeric_transformer = Pipeline(steps=[
        ('imputer', SimpleImputer(strategy='median')),
        ('scaler', StandardScaler())
    ])

    categorical_transformer = Pipeline(steps=[
        ('imputer', SimpleImputer(strategy='constant', fill_value='missing')),
        ('onehot', OneHotEncoder(handle_unknown='ignore', sparse_output=False))
    ])

    preprocessor = ColumnTransformer(
        transformers=[
            ('num', numeric_transformer, numeric_features),
            ('cat', categorical_transformer, categorical_features)
        ],
        remainder='drop'
    )

    # Model: Logistic Regression (CPU-friendly, low energy, fast convergence)
    # Using 'lbfgs' solver as it's efficient for small-medium datasets
    clf = Pipeline(steps=[
        ('preprocessor', preprocessor),
        ('classifier', LogisticRegression(max_iter=1000, multi_class='auto', solver='lbfgs', n_jobs=1))
    ])

    # Train
    clf.fit(X_train, y_train)

    # Predict and Evaluate
    y_pred = clf.predict(X_test)
    accuracy = accuracy_score(y_test, y_pred)
    
    print(f"ACCURACY={accuracy:.6f}")

if __name__ == "__main__":
    run_pipeline("data/wine_dataset.csv")

