import os
import numpy as np
import pandas as pd
import lightgbm as lgb
from sklearn.model_selection import StratifiedKFold
from sklearn.metrics import accuracy_score

data_path = './datasets/'

train = pd.read_csv(os.path.join(data_path, 'train.csv'))
if train.columns.size <= 2:
    train = pd.read_csv(os.path.join(data_path, 'train.csv'), sep=';', decimal=',')

test = pd.read_csv(os.path.join(data_path, 'test.csv'))
if test.columns.size <= 2:
    test = pd.read_csv(os.path.join(data_path, 'test.csv'), sep=';', decimal=',')

ycol = 'label'
features = [x for x in train.columns if x not in [ycol, 'id']]

NFOLD = 5
num_class = 9
random_state = 2021
KF = StratifiedKFold(n_splits=NFOLD, shuffle=True, random_state=random_state)

train_features = train[features].values
train_label = train[ycol].values
test_features = test[features].values

def custom_accuracy_eval(y_hat, data):
    y_true = data.get_label()
    y_hat = y_hat.reshape(num_class, -1).T
    return 'accuracy', accuracy_score(y_true, np.argmax(y_hat, axis=1)), True

params_lgb = {
    'boosting': 'gbdt',
    'objective': 'multiclass',
    'num_class': num_class,
    'metric': 'multi_logloss',
    'first_metric_only': True,
    'force_row_wise': True,
    'random_state': random_state,
    'learning_rate': 0.05,
    'subsample': 0.8,
    'subsample_freq': 3,
    'colsample_bytree': 0.8,
    'max_depth': 6,
    'num_leaves': 31,
    'n_jobs': -1,
    'verbose': -1,
}

oof_lgb = np.zeros([len(train), num_class])
predictions_lgb = np.zeros([len(test), num_class])

for fold_, (trn_idx, val_idx) in enumerate(KF.split(train_features, train_label)):
    trn_data = lgb.Dataset(train_features[trn_idx], label=train_label[trn_idx])
    val_data = lgb.Dataset(train_features[val_idx], label=train_label[val_idx], reference=trn_data)

    train_kwargs = dict(
        params=params_lgb,
        train_set=trn_data,
        valid_sets=[trn_data, val_data],
        valid_names=('train', 'val'),
        num_boost_round=50000,
        feval=custom_accuracy_eval,
    )

    try:
        clf_lgb = lgb.train(
            **train_kwargs,
            early_stopping_rounds=200,
            verbose_eval=False,
        )
    except TypeError:
        clf_lgb = lgb.train(
            **train_kwargs,
            callbacks=[lgb.early_stopping(200), lgb.log_evaluation(0)],
        )

    best_iter = clf_lgb.best_iteration
    oof_lgb[val_idx] = clf_lgb.predict(train_features[val_idx], num_iteration=best_iter)
    predictions_lgb += clf_lgb.predict(test_features, num_iteration=best_iter) / NFOLD

test['label'] = np.argmax(predictions_lgb, axis=1)
test[['id', 'label']].to_csv('./submit.csv', index=False)

accuracy = accuracy