    def is_num_str(x):
        try:
            float(str(x))
            return True
        except Exception:
            return False
    if df.shape[1] > 0:
        num_ratio = np.mean([is_num_str(c) for c in df.columns])
        alpha_ratio = np.mean([bool(re.search('[A-Za-z]', str(c))) for c in df.columns])
    else:
        num_ratio = 0
        alpha_ratio = 0
    if num_ratio > 0.5 or alpha_ratio < 0.1:
        sep = ','
        dec = '.'
