    numeric_ratio = pd.to_numeric(y, errors='coerce').notna().mean()
    if numeric_ratio >= 0.9 and unique > max(20, int(0.05 * n)):
        return False
    if y.dtype == 'object' or str(y.dtype) == 'bool' or str(y.dtype).startswith('category'):
        return True
    return unique <= max(20, int(0.05 * n))

