import numpy as np
import pandas as pd
from sklearn.model_selection import train_test_split
from sklearn.preprocessing import StandardScaler
from sklearn.linear_model import LogisticRegression
from sklearn.pipeline import Pipeline
from sklearn.metrics import accuracy_score

# Load dataset (first 60 columns are features, last column is the label)
data = pd.read_csv('Sonar Data.csv', header=None)
X = data.iloc[:, :60]      # feature columns
y = data.iloc[:, 60]       # target column (R or M)

# Split into training and test sets with stratification to preserve class balance
X_train, X_test, y_train, y_test = train_test_split(
    X, y, test_size=0.3, random_state=42, stratify=y
)

# Create a lightweight preprocessing + model pipeline
# StandardScaler normalises features; LogisticRegression with liblinear solver is
# efficient for small, low-dimensional datasets and runs quickly on CPU.
model = Pipeline([
    ('scaler', StandardScaler()),
    ('clf', LogisticRegression(
        solver='liblinear',  # fast solver for small data
        max_iter=200,        # few iterations to keep computation low
        random_state=42
    ))
])

# Train model
model.fit(X_train, y_train)

# Evaluate on test set
y_pred = model.predict(X_test)
accuracy = accuracy_score(y_test, y_pred)

# Output only the required accuracy
print(f"ACCURACY={accuracy:.6f}")