# Build lightweight pipeline: StandardScaler + GaussianNB
# GaussianNB is the most appropriate for continuous iris features and is extremely efficient
pipeline = Pipeline([
    ("scaler", StandardScaler()),
    ("gnb", GaussianNB()),
])

pipeline.fit(X_train, y_train)
