import numpy as np
import pandas as pd
from sklearn.model_selection import train_test_split, GridSearchCV
from sklearn.ensemble import RandomForestClassifier, AdaBoostClassifier, GradientBoostingClassifier
from sklearn.metrics import accuracy_score, classification_report, confusion_matrix
from imblearn.over_sampling import SMOTE

data = pd.read_csv("EireJet.csv")
data = data.dropna()

cat_maps = {
    'Gender': {'Female': 1, 'Male': 0},
    'Frequent Flyer': {'Yes': 1, 'No': 0},
    'Type of Travel': {'Personal Travel': 1, 'Business travel': 0},
    'Class': {'Eco': 0, 'Eco Plus': 1, 'Business': 2},
    'satisfaction': {'neutral or dissatisfied': 0, 'satisfied': 1}
}
for col, mapping in cat_maps.items():
    data[col] = data[col].map(mapping)

X = data.drop('satisfaction', axis=1)
y = data['satisfaction']

X_train, X_test, y_train, y_test = train_test_split(X, y, test_size=0.3, random_state=100, stratify=y)

smote = SMOTE(random_state=101)
X_train, y_train = smote.fit_resample(X_train, y_train)

def evaluate(estimator, name, params):
    gs = GridSearchCV(estimator, params, scoring='accuracy', cv=5)
    gs.fit(X_train, y_train)
    print(f"{name} BEST_PARAMS={gs.best_params_}")
    print(f"{name} BEST_SCORE={gs.best_score_:.6f}")
    best = gs.best_estimator_
    best.fit(X_train, y_train)
    y_pred = best.predict(X_test)
    acc = accuracy_score(y_test, y_pred)
    print(f"{name} ACCURACY={acc:.6f}")
    print(classification_report(y_test, y_pred))
    print("Confusion matrix:\n", confusion_matrix(y_test, y_pred))

rf_params = {'n_estimators': [50, 100, 150], 'max_features': ['auto', 'sqrt', 'log2']}
evaluate(RandomForestClassifier(criterion='entropy', random_state=1), "RandomForest", rf_params)

ada_params = {'n_estimators': [30, 35, 40, 45, 50, 55, 60]}
evaluate(AdaBoostClassifier(random_state=1), "AdaBoost", ada_params)

gb_params = {'n_estimators': [100, 150, 200], 'max_depth': [9, 10, 11], 'max_leaf_nodes': [8, 12, 16, 20]}
evaluate(GradientBoostingClassifier(random_state=1), "GradientBoost", gb_params)