import pandas as pd
import numpy as np
from sklearn.model_selection import train_test_split
from sklearn.feature_extraction.text import TfidfVectorizer
from sklearn.naive_bayes import MultinomialNB
from sklearn.pipeline import Pipeline
from sklearn.metrics import accuracy_score

# Robust CSV loading
df = None
for kwargs in [dict(), dict(sep=';', decimal=',')]:
    try:
        df = pd.read_csv('voting_data.csv', **kwargs)
        if df.shape[1] < 2:
            raise ValueError("Too few columns")
        break
    except Exception:
        continue

if df is None or df.empty:
    raise RuntimeError("Failed to load dataset")

# Normalize column names
df.columns = [c.strip().replace('  ', ' ') for c in df.columns]
df = df[[c for c in df.columns if not c.startswith('Unnamed')]]

# Identify text and label columns
text_col = None
label_col = None

if 'text' in df.columns:
    text_col = 'text'
if 'label' in df.columns:
    label_col = 'label'

# Fallback: first object-like column with long strings as text, another as label
if text_col is None or label_col is None:
    obj_cols = df.select_dtypes(include=['object']).columns.tolist()
    if len(obj_cols) >= 2:
        avg_lens = {c: df[c].astype(str).str.len().mean() for c in obj_cols}
        sorted_cols = sorted(avg_lens, key=avg_lens.get, reverse=True)
        text_col = text_col or sorted_cols[0]
        label_col = label_col or sorted_cols[1]
    elif len(obj_cols) == 1:
        text_col = text_col or obj_cols[0]
        num_cols = df.select_dtypes(include='number').columns.tolist()
        if num_cols:
            label_col = label_col or num_cols[0]

assert text_col is not None and label_col is not None, "Cannot identify text and label columns"

# Drop rows with missing text or label
df = df.dropna(subset=[text_col, label_col])
df[text_col] = df[text_col].astype(str)
df[label_col] = df[label_col].astype(str).str.strip()

# Ensure at least 2 classes
n_classes = df[label_col].nunique()
assert n_classes >= 2, "Need at least 2 classes for classification"

assert len(df) > 0, "Dataset is empty after preprocessing"

X = df[text_col]
y = df[label_col]

X_train, X_test, y_train, y_test = train_test_split(
    X, y, test_size=0.25, random_state=42, stratify=y if n_classes >= 2 else None
)

assert len(X_train) > 0 and len(X_test) > 0, "Train/test split resulted in empty set"

# Pipeline: TF-IDF (lightweight settings) + Multinomial Naive Bayes
# Using sublinear_tf and limiting max_features for energy efficiency
pipeline = Pipeline([
    ('tfidf', TfidfVectorizer(
        max_features=10000,
        sublinear_tf=True,
        ngram_range=(1, 2),
        strip_accents='unicode',
        lowercase=True,
        dtype=np.float32  # Use float32 to reduce memory
    )),
    ('clf', MultinomialNB(alpha=0.1))
])

pipeline.fit(X_train, y_train)
y_pred = pipeline.predict(X_test)

accuracy = accuracy_score(y_test, y_pred)
print(f"ACCURACY={accuracy:.6f}")

# OPTIMIZATION SUMMARY
# 1. Model choice: Multinomial