import os
import pandas as pd
import numpy as np
from sklearn.model_selection import train_test_split
from sklearn.impute import SimpleImputer
from sklearn.pipeline import Pipeline
from sklearn.naive_bayes import GaussianNB
from sklearn.preprocessing import LabelEncoder
from sklearn.metrics import accuracy_score

justification = (
    "Simple imputation with a lightweight GaussianNB classifier is CPU-efficient "
    "and well-suited for small, mostly binary numeric features."
)

dataset_path = os.environ.get("DATASET_PATH")
if dataset_path is None or not os.path.exists(dataset_path):
    candidates = ["dataset.csv", "data.csv", "zoo.csv"]
    dataset_path = next((c for c in candidates if os.path.exists(c)), None)
    if dataset_path is None:
        csv_files = [f for f in os.listdir(".") if f.endswith(".csv")]
        if not csv_files:
            raise FileNotFoundError("No CSV dataset file found.")
        dataset_path = csv_files[0]

df = pd.read_csv(dataset_path)

if "animal_name" in df.columns:
    df = df.drop(columns=["animal_name"])

if "class_type" not in df.columns:
    raise ValueError("Target column 'class_type' not found.")

X = df.drop(columns=["class_type"])
y = df["class_type"]

X = X.apply(pd.to_numeric, errors="coerce")

if y.dtype == object or str(y.dtype).startswith("category"):
    y = LabelEncoder().fit_transform(y)
else:
    y = pd.to_numeric(y, errors="coerce")

mask = ~pd.isnull(y)
X = X.loc[mask]
y = np.array(y)[mask]

value_counts = pd.Series(y).value_counts()
stratify = y if value_counts.min() >= 2 else None

test_size = 0.2 if len(X) > 10 else 0.3
X_train, X_test, y_train, y_test = train_test_split(
    X, y, test_size=test_size, random_state=42, stratify=stratify
)

model = Pipeline(
    [
        ("imputer", SimpleImputer(strategy="most_frequent")),
        ("classifier", GaussianNB()),
    ]
)

model.fit(X_train, y_train)
predictions = model.predict(X_test)
accuracy = accuracy_score(y_test, predictions)
print(f"ACCURACY={accuracy:.6f}")