import pandas as pd
import numpy as np
from sklearn.preprocessing import StandardScaler
from sklearn.linear_model import LogisticRegression
from sklearn.metrics import accuracy_score
import joblib

def main():
    X_train = pd.read_csv("cancer_data.csv").values
    y_train = pd.read_csv("cancer_data_y.csv").values.ravel()
    X_test = pd.read_csv("test_cancer_data.csv").values
    y_test = pd.read_csv("test_cancer_data_y.csv").values.ravel()
    scaler = StandardScaler()
    X_train_scaled = scaler.fit_transform(X_train)
    X_test_scaled = scaler.transform(X_test)
    model = LogisticRegression(solver='liblinear', max_iter=200, n_jobs=1)
    model.fit(X_train_scaled, y_train)
    y_pred = model.predict(X_test_scaled)
    accuracy = accuracy_score(y_test, y_pred)
    print(f"ACCURACY={accuracy:.6f}")
    joblib.dump({'model': model, 'scaler': scaler}, 'trained_model.pkl')

if __name__ == "__main__":
    main()