import pandas as pd
import numpy as np
from sklearn.preprocessing import StandardScaler
from sklearn.linear_model import LogisticRegression
from sklearn.metrics import accuracy_score

def main():
    X_train = pd.read_csv("cancer_data.csv", header=None).values
    y_train = pd.read_csv("cancer_data_y.csv", header=None).values.ravel()
    X_test = pd.read_csv("test_cancer_data.csv", header=None).values
    y_test = pd.read_csv("test_cancer_data_y.csv", header=None).values.ravel()

    scaler = StandardScaler()
    X_train = scaler.fit_transform(X_train)
    X_test = scaler.transform(X_test)

    clf = LogisticRegression(max_iter=200, solver='liblinear')
    clf.fit(X_train, y_train)

    y_pred = clf.predict(X_test)
    acc = accuracy_score(y_test, y_pred)

    print(f"ACCURACY={acc:.6f}")

if __name__ == "__main__":
    main()