# Energyâ€‘efficient breast cancer classification
# - Uses a lightweight Logistic Regression (liblinear solver)
# - Standard scaling applied via a Pipeline for reproducibility
# - Only the final accuracy is printed as required
# - No plots, interactive input, or large embeddings are used
# - All operations run on CPU and are computationally light

import pandas as pd
from sklearn.model_selection import train_test_split
from sklearn.preprocessing import StandardScaler
from sklearn.linear_model import LogisticRegression
from sklearn.pipeline import Pipeline
from sklearn.metrics import accuracy_score
import joblib

# Load dataset
try:
    df = pd.read_csv('Cancer_Data.csv')
except FileNotFoundError:
    raise FileNotFoundError("Cancer_Data.csv not found. Ensure the file is in the working directory.")

# Convert diagnosis labels to numeric: M -> 1, B -> 0
df['diagnosis'] = df['diagnosis'].map({'M': 1, 'B': 0})
y = df['diagnosis']

# Use all available numeric feature columns (excluding id and diagnosis)
feature_columns = [col for col in df.columns if col not in ['id', 'diagnosis']]
X = df[feature_columns]

# Split into training and test sets
X_train, X_test, y_train, y_test = train_test_split(
    X, y, test_size=0.3, random_state=42, stratify=y)

# Build a simple pipeline: scaling + logistic regression
pipe = Pipeline([
    ('scaler', StandardScaler()),
    ('clf', LogisticRegression(
        solver='liblinear',  # efficient for small datasets
        random_state=42,
        max_iter=200
    ))
])

# Train the model
pipe.fit(X_train, y_train)

# Predict on the test set
y_pred = pipe.predict(X_test)

# Evaluate accuracy
accuracy = accuracy_score(y_test, y_pred)
print(f"ACCURACY={accuracy:.6f}")

# (Optional) Persist the trained pipeline for future use
joblib.dump(pipe, 'breast_cancer_pipeline.joblib')