import pandas as pd
import numpy as np
from sklearn.model_selection import train_test_split
from sklearn.preprocessing import StandardScaler, LabelEncoder
from sklearn.ensemble import GradientBoostingClassifier
from sklearn.metrics import accuracy_score
from sklearn.impute import SimpleImputer

# Load dataset
df = pd.read_csv("./dataset.csv")

# Strip whitespace from column names
df.columns = df.columns.str.strip()

# Identify target and features
# "brand" appears to be the categorical target for classification
target_col = "brand"

# Separate features and target
X = df.drop(columns=[target_col])
y = df[target_col].str.strip() if df[target_col].dtype == object else df[target_col]

# Encode target labels
le = LabelEncoder()
y_encoded = le.fit_transform(y)

# Handle numeric columns: convert to numeric, coerce errors to NaN
for col in X.columns:
    X[col] = pd.to_numeric(X[col].astype(str).str.strip(), errors="coerce")

# Impute missing values with median (energy-efficient, robust to outliers)
imputer = SimpleImputer(strategy="median")
X_imputed = imputer.fit_transform(X)

# Scale features (lightweight StandardScaler, helps gradient boosting converge)
scaler = StandardScaler()
X_scaled = scaler.fit_transform(X_imputed)

# Train/test split with stratification
X_train, X_test, y_train, y_test = train_test_split(
    X_scaled, y_encoded, test_size=0.2, random_state=42, stratify=y_encoded
)

# Use GradientBoostingClassifier with modest hyperparameters
# Chosen over deep learning for CPU efficiency and small dataset suitability
# Shallow trees and limited estimators keep energy usage low
model = GradientBoostingClassifier(
    n_estimators=150,
    max_depth=4,
    learning_rate=0.1,
    subsample=0.8,
    min_samples_split=5,
    min_samples_leaf=3,
    random_state=42,
)

model.fit(X_train, y_train)

# Predict and evaluate
y_pred = model.predict(X_test)
accuracy = accuracy_score(y_test, y_pred)

print(f"ACCURACY={accuracy:.6f}")

# OPTIMIZATION SUMMARY
# 1. Preprocessing: Used SimpleImputer(median) and StandardScaler — both lightweight O(n) transforms.
# 2. Model: GradientBoostingClassifier with 150 estimators and max_depth=4 — effective for tabular
#    data on small datasets, runs efficiently on CPU without GPU requirements.
# 3. Avoided deep learning, large embeddings, or heavy feature engineering to minimize energy usage.
# 4. Subsample=0.8 reduces per-iteration computation (stochastic gradient boosting).
# 5. Stratified split ensures balanced evaluation even with imbalanced brand classes.
# 6. All operations are deterministic (random_state=42) for reproducibility.