DATASET_PATH = "diabetes.csv"
DATASET_HEADERS = [
    "Pregnancies",
    "Glucose",
    "BloodPressure",
    "SkinThickness",
    "Insulin",
    "BMI",
    "DiabetesPedigreeFunction",
    "Age",
    "Outcome",
]
RANDOM_STATE = 42
np.random.seed(RANDOM_STATE)


def read_csv_robust(path, expected_headers):
    df = pd.read_csv(path)
    df.columns = [str(c).strip() for c in df.columns]
    used_sep = ","
    used_decimal = "."
    if df.shape[1] == 1:
        df = pd.read_csv(path, sep=";", decimal=",")
        df.columns = [str(c).strip() for c in df.columns]
        used_sep = ";"
        used_decimal = ","
    overlap = len(set(df.columns) & set(expected_headers))
    if df.shape[1] == len(expected_headers) and overlap == 0:
        df = pd.read_csv(
            path, sep=used_sep, decimal=used_decimal, header=None, names=expected_headers
        )
    return df


def prepare_data(df, expected_headers):
    target_col = "Outcome" if "Outcome" in df.columns else df.columns[-1]
    feature_cols = [c for c in expected_headers if c in df.columns and c != target_col]
    if not feature_cols:
        feature_cols = [c for c in df.columns if c != target_col]
    cols_missing = [
        c
        for c in ["Glucose", "BloodPressure", "SkinThickness", "Insulin", "BMI"]
        if c in df.columns
    ]
    if cols_missing:
        df[cols_missing] = df[cols_missing].replace(0, np.nan)
        fill_values = {}
        if "Glucose" in cols_missing:
            fill_values["Glucose"] = df["Glucose"].mean()
        for col in ["BloodPressure", "SkinThickness", "Insulin", "BMI"]:
            if col in cols_missing:
                fill_values[col] = df[col].median()
        if fill_values:
            df.fillna(fill_values, inplace=True)
    return df, feature_cols, target_col


def main():
    df = read_csv_robust(DATASET_PATH, DATASET_HEADERS)
