import pandas as pd
from sklearn.model_selection import train_test_split
from sklearn.preprocessing import LabelEncoder
from sklearn.metrics import accuracy_score
from sklearn.tree import DecisionTreeClassifier

# Load dataset
df = pd.read_csv("./data/student-mat.csv")

# Rename columns for clarity
df.columns = [
    "school", "sex", "age", "address", "family_size", "parents_status",
    "mother_education", "father_education", "mother_job", "father_job",
    "reason", "guardian", "commute_time", "study_time", "failures",
    "school_support", "family_support", "paid_classes", "activities",
    "nursery", "desire_higher_edu", "internet", "romantic",
    "family_quality", "free_time", "go_out", "weekday_alcohol_usage",
    "weekend_alcohol_usage", "health", "absences", "period1_score",
    "period2_score", "final_score"
]

# Create categorical target based on final_score
df["final_grade"] = "na"
df.loc[(df.final_score >= 15) & (df.final_score <= 20), "final_grade"] = "good"
df.loc[(df.final_score >= 10) & (df.final_score <= 14), "final_grade"] = "fair"
df.loc[(df.final_score >= 0)  & (df.final_score <= 9),  "final_grade"] = "poor"

# Encode target to integers
label_enc = LabelEncoder()
df["final_grade"] = label_enc.fit_transform(df["final_grade"])

# One‑hot encode all remaining categorical columns
X = pd.get_dummies(df.drop(columns=["final_score", "final_grade"]), drop_first=True)
y = df["final_grade"]

# Train‑test split (70/30)
X_train, X_test, y_train, y_test = train_test_split(
    X, y, test_size=0.3, random_state=42, stratify=y
)

# Energy‑efficient model: shallow decision tree
clf = DecisionTreeClassifier(
    max_depth=5,
    min_samples_leaf=17,
    random_state=42,
    criterion="entropy"
)
clf.fit(X_train, y_train)

# Predict and evaluate
y_pred = clf.predict(X_test)
accuracy = accuracy_score(y_test, y_pred)

# Print only the required metric
print(f"ACCURACY={accuracy:.6f}")

